import './App.css';
import React,{Component} from "react";
import axios from "axios";

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      searchText: "",
      loading: true,
      error: false,
      apiData: [],
      dupApiData: [],
      newNote: "",
      newCategory: "",
      updateNote: "",
      updateCategory: "",
      updateId: "",
      message: ""
    }
  };

  componentDidMount(){
    this.getApiData();
  }

  getApiData = () =>{
    let self = this;
    axios.get('http://127.0.0.1:8000/api/notes/').then((res)=>{
      console.log("res data is ");
      console.log(res.data);
      console.log(res.data.notes)
      self.setState({
        apiData: res.data.notes,
        loading: false,
        error: false,
      })
    }).catch((err)=>{
      console.log(err);
      self.setState({ error: true, loading: false })
    })
  }

  handleCreateNote = () =>{
    let self = this;
    axios.post('http://127.0.0.1:8000/api/notes/',{
      content: this.state.newNote,
      category: this.state.newCategory,
    }).then((res)=>{
      console.log("create note response ");
      console.log(res.data);
      self.setState({ message: "Note Created Successfully"})
      self.getApiData();
    }).catch((err)=>{
      console.log(err);
      self.setState({ error: true, loading: false })
    })
  }

  handleUpdateNote = () =>{
    let self = this;
    axios.put(`http://127.0.0.1:8000/api/notes/${this.state.updateId}/`,{
      content: this.state.updateNote,
      category: this.state.updateCategory,
    }).then((res)=>{
      console.log("update note response ");
      console.log(res.data);
      self.setState({ message: "Note Updated Successfully"})
      self.getApiData();
    }).catch((err)=>{
      console.log(err);
      self.setState({ error: true, loading: false })
    })
  }

  handleDeleteNote = (id) =>{
    let self = this;
    axios.delete(`http://127.0.0.1:8000/api/notes/${id}/`).then((res)=>{
      console.log("delete note response ");
      console.log(res.data);
      self.setState({ message: "Note Deleted Successfully"})
      self.getApiData();
    }).catch((err)=>{

      self.setState({ error: true, message: "Error while Deleting the Note"})
    })
    }



    render(){
    return(
    <div>
    <div>App</div>
    {
    this.state.loading ?
    <div> data is loading </div> :
    this.state.error ? <div> Error, Something went wrong </div> :

    <div>
      {/* <input onChange={this.handleChange}/>
      <button onClick={this.handleSearch}>Search</button> */}
      {this.state.apiData.map((data)=>
      <div>
        <ul>
          <li>{data.id}  &nbsp;{data.content}</li>
          <li>{data.category}</li>
        </ul>
        <button onClick={() => this.handleUpdateNote(data.id)}>Update</button>
        <button onClick={() => this.handleDeleteNote(data.id)}>Delete</button>
        </div>
      )}

      {<div> {this.state.message}</div>}    
    </div>

    }
  </div>
)}

}
export default App;
