import React, {Component} from "react";
import axios from 'axios';

class Signup extends Component {
    constructor(props){
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            error: null
        };
    }

    handleUsernameChange = (event) => {
        this.setState({ username: event.target.value });
    };

    handleEmailChange = (event) => {
        this.setState({ email: event.target.value });
    };

    handlePasswordChange = (event) => {
        this.setState({ password: event.target.value });
    };

    handleSignup = (event) => {
        event.preventDefault();

        axios
          .post('http://localhost:8000/authapp/signup/', {
            username: this.state.username,
            email: this.state.email,
            password: this.state.password,
          })
          .then((response) => {
            // If signup is successful, redirect to the login page
            this.props.history.push('/login');
          })
          .catch((error) => {
            if (error.response.status === 400) {
              // If signup fails, show an error message and reset the password field
              this.setState({ error: 'Username or email is already taken. Please try again with different credentials.' });
              this.setState({ password: '' });
            } else {
              // If there is a different error, handle it here
            }
          });
    };

    render() {
        return (
            <div>
                <h3>Signup</h3>
                <form onSubmit={this.handleSignup}>
                    <div>
                        <label>Username:</label>
                        <input type="text" value={this.state.username} onChange={this.handleUsernameChange} required />
                    </div>
                    <div>
                        <label>Email:</label>
                        <input type="email" value={this.state.email} onChange={this.handleEmailChange} required />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input type="password" value={this.state.password} onChange={this.handlePasswordChange} required />
                    </div>
                    {this.state.error && <p>{this.state.error}</p>}
                    <div>
                        <button type="submit">Signup</button>
                    </div>
                </form>
            </div>
        );
    }
}

export default Signup;
