import './App.css';
import React,{Component} from "react";
import axios from "axios";

class App extends Component{
constructor(props){
super(props);
this.state = {
searchText: "",
loading: true,
error: false,
apiData: [],
dupApiData: [],
}
};

componentDidMount(){
this.getApiData();
}

getApiData = () =>{
let self = this;
axios.get('http://127.0.0.1:8000/api/notes/').then((res)=>{
console.log("res data is ");

  console.log(res.data);
  console.log(res.data.notes)
  self.setState({
    apiData: res.data.notes,
    loading: false,
    error: false,
  })
}).catch((err)=>{
  console.log(err);
  self.setState({ error: true, loading: false })
})
}

handleUpdate = (data) => {
// your update logic here, example:
axios.put('http://127.0.0.1:8000/api/notes/'+ data.id + '/', data)
.then((response) => {
console.log(response.data);
this.getApiData();
})
.catch((error) => {
console.log(error);
});
}

handleDelete = (data) => {
// your delete logic here, example:
axios.delete('http://127.0.0.1:8000/api/notes/'+ data.id + '/')
.then((response) => {
console.log(response.data);
this.getApiData();
})
.catch((error) => {
console.log(error);
});
}

render(){
return(
<div>
<div>App</div>
{
this.state.loading ?
<div> data is loading </div> :
    this.state.error ? <div> Error, Something went wrong </div> :

    <div>
      {this.state.apiData.map((data)=>
      <div>
        <ul>
          <li>{data.id}  &nbsp;{data.content}</li>
          <li>{data.category}</li>
        </ul>
        <button onClick={() => this.handleUpdate(data)}>Update</button>
        <button onClick={() => this.handleDelete(data)}>Delete</button>
        </div>
      )}
    </div>
    }
  </div>
)
}
}

export default App;
