import './App.css';
import React,{Component} from "react";
import axios from "axios";

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      searchText: "",
      loading: true,
      error: false,
      apiData: [],
      dupApiData: [],
      newContent: "",
      newCategory: "",
      selectedData: {},
    }
  };

  componentDidMount(){
    this.getApiData();
  }

  getApiData = () =>{
    let self = this;
    axios.get('http://127.0.0.1:8000/api/notes/').then((res)=>{
      console.log("res data is ");

      console.log(res.data);
      console.log(res.data.notes)
      self.setState({
        apiData: res.data.notes,
        loading: false,
        error: false,
      })
    }).catch((err)=>{
      console.log(err);
      self.setState({ error: true, loading: false })
    })
  }

  handleAddNote = () => {
    axios.post('http://127.0.0.1:8000/api/notes/', {
      content: this.state.newContent,
      category: this.state.newCategory,
    }).then((res) => {
      console.log(res.data);
      this.getApiData();
    }).catch((err) => {
      console.log(err);
    });
  }

  handleUpdateNote = () => {
    axios.put(`http://127.0.0.1:8000/api/notes/${this.state.selectedData.id}/`, {
      content: this.state.selectedData.content,
      category: this.state.selectedData.category,
    }).then((res) => {
      console.log(res.data);
      this.getApiData();
    }).catch((err) => {
      console.log(err);
    });
  }

  handleDeleteNote = (data) => {
    axios.delete(`http://127.0.0.1:8000/api/notes/${data.id}/`).then((res) => {
      console.log(res.data);
      this.getApiData();
    }).catch((err) => {
      console.log(err);
    });
  }

  handleInputChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  }

  handleSelectNote = (data) => {
    this.setState({ selectedData: data });
  }

  render(){
    return(
      <div>
        <div>App</div>
        <div>
          <input 
            type="text" 
            name="searchText"
            placeholder="Search..."
            value={this.state.searchText}
            onChange={this.handleChange}
            />
            <button onClick={this.handleSearch}>Search</button>
            </div>
            {
            this.state.loading ?
            <div>Data is loading...</div> :
            this.state.error ? <div> Error, Something went wrong </div> :

            <div>
              {this.state.apiData.map((data)=>
                <div key={data.id}>
                  <ul>
                    <li>{data.id} &nbsp;{data.content}</li>
                    <li>{data.category}</li>
                    <li>
                      <button onClick={() => this.handleEdit(data.id)}>Edit</button>
                      &nbsp;
                      <button onClick={() => this.handleDelete(data.id)}>Delete</button>
                    </li>
                  </ul>
                </div>
              )}
              {<div>{this.state.message}</div>}
            </div>
          }
        </div>
      )
    }

    handleChange = (e) =>{
    this.setState({
    searchText: e.target.value
    })
    };

    handleSearch = () =>{
    let filteredData = this.state.dupApiData.filter((data)=>{
    return data.content.includes(this.state.searchText) || data.category.includes(this.state.searchText)
    });
    this.setState({
    apiData: filteredData
    });
    };

    handleEdit = (id) =>{
    this.props.history.push(`/edit/${id}`);
    };

    handleDelete = (id) =>{
    let self = this;
    axios.delete('http://127.0.0.1:8000/api/notes/${id}/').then(res => {
    console.log(res);
    self.setState({ message: "Note deleted successfully" });
    self.getApiData();
    })
    .catch(err => {
    console.log(err);
    self.setState({ message: "Error deleting note" });
    });
    };
    }

    export default App;
