import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const [newNote, setNewNote] = useState({ content: '', category: '' });
  const [updateNote, setUpdateNote] = useState({ id: '', content: '', category: '' });
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    const fetchNotes = async () => {
      setLoading(true);
      setError(false);
      try {
        const res = await axios.get('http://127.0.0.1:8000/api/notes/');
        setNotes(res.data.notes);
        setLoading(false);
      } catch (err) {
        setError(true);
        setLoading(false);
      }
    };
    fetchNotes();
  }, []);

  const handleCreateNote = async () => {
    try {
      const res = await axios.post('http://127.0.0.1:8000/api/notes/', {
        content: newNote.content,
        category: newNote.category,
      });
      setNotes([...notes, res.data]);
      setNewNote({ content: '', category: '' });
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpdateNote = async () => {
    try {
      const res = await axios.put(`http://127.0.0.1:8000/api/notes/${updateNote.id}/`, {
        content: updateNote.content,
        category: updateNote.category,
      });
      const updatedNotes = notes.map((note) => (note.id === updateNote.id ? res.data : note));
      setNotes(updatedNotes);
      setUpdateNote({ id: '', content: '', category: '' });
      setEditing(false);
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteNote = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/notes/${id}/`);
      setNotes(notes.filter((note) => note.id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleEditClick = (note) => {
    setUpdateNote({ id: note.id, content: note.content, category: note.category });
    setEditing(true);
  };

  const renderUpdateForm = () => {
    return (
      <form>
        <input type="text" placeholder="ID" value={updateNote.id} disabled />
        <input
          type="text"
          placeholder="Content"
          value={updateNote.content}
          onChange={(e) => setUpdateNote({ ...updateNote, content: e.target.value })}/>
<input
type="text"
placeholder="Category"
value={updateNote.category}
onChange={(e) => setUpdateNote({ ...updateNote, category: e.target.value })}
/>
<button onClick={handleUpdateNote}>Save</button>
</form>
);
};

return (
<div>
{loading && <div>Loading...</div>}
{error && <div>An error occurred while fetching the notes.</div>}
{editing ? (
renderUpdateForm()
) : (
<form>
<input
type="text"
placeholder="Content"
value={newNote.content}
onChange={(e) => setNewNote({ ...newNote, content: e.target.value })}
/>
<input
type="text"
placeholder="Category"
value={newNote.category}
onChange={(e) => setNewNote({ ...newNote, category: e.target.value })}
/>
<button onClick={handleCreateNote}>Create Note</button>
</form>
)}
{notes.map((note) => (
<div key={note.id}>
<h3>{note.content}</h3>
<p>{note.category}</p>
<button onClick={() => handleEditClick(note)}>Edit</button>
<button onClick={() => handleDeleteNote(note.id)}>Delete</button>
</div>
))}
</div>
);
};

export default App;
