import React, { Component } from 'react';
import axios from 'axios';

class App extends Component {
  state = {
    data: [],
    loading: false
  };

  componentDidMount() {
    this.getData();
  }

  getData = () => {
    this.setState({ loading: true });
    axios.get('http://127.0.0.1:8000/api/notes/')
      .then(res => {
        this.setState({ data: res.data, loading: false });
      })
      .catch(err => {
        console.error(err);
        this.setState({ loading: false });
      });
  };

  createData = (newData) => {
    axios.post('http://127.0.0.1:8000/api/notes/', newData)
      .then(res => {
        this.getData();
      })
      .catch(err => {
        console.error(err);
      });
  };

  updateData = (id, updatedData) => {
    axios.put('http://127.0.0.1:8000/api/notes/${id}', updatedData)
      .then(res => {
        this.getData();
      })
      .catch(err => {
        console.error(err);
      });
  };

  deleteData = (id) => {
    axios.delete('http://127.0.0.1:8000/api/notes/${id}')
      .then(res => {
        this.getData();
      })
      .catch(err => {
        console.error(err);
      });
  };

  render() {
    const { data, loading } = this.state;

    return (
      <div>
        {loading && <p>Loading...</p>}
        <ul>
          {data.map(item => (
            <li key={item}>
              {item.content}
              <button onClick={() => this.updateData(item.id, { name: 'Updated Name' })}>Update</button>
              <button onClick={() => this.deleteData(item.id)}>Delete</button>
            </li>
          ))}
        </ul>
        <button onClick={() => this.createData({ name: 'New Name' })}>Create</button>
      </div>
    );
  }
}

export default App;
