import React, { Component } from 'react';
import axios from 'axios';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      error: null,
    };
  }

  handleUsernameChange = (event) => {
    this.setState({ username: event.target.value });
  };

  handlePasswordChange = (event) => {
    this.setState({ password: event.target.value });
  };

  handleLogin = (event) => {
    event.preventDefault();

    axios
      .post('/login/', {
        username: this.state.username,
        password: this.state.password,
      })
      .then((response) => {
        // If login is successful, do something (e.g., redirect to home page)
      })
      .catch((error) => {
        if (error.response.status === 401) {
          // If login fails, show an error message and take user to the registration page
          this.setState({ error: 'Invalid credentials. Please register.' });
          this.props.history.push('/register');
        } else {
          // If there is a different error, handle it here
        }
      });
  };

  render() {
    return (
      <form onSubmit={this.handleLogin}>
        <div>
          <label>Username:</label>
          <input type="text" value={this.state.username} onChange={this.handleUsernameChange} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={this.state.password} onChange={this.handlePasswordChange} />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
        {this.state.error && <p>{this.state.error}</p>}
      </form>
    );
  }
}

export default Login;
