import React, { Component } from 'react';
import axios from 'axios';
// import '.../App.css';

class Crud extends Component {
  state = {
    data: [],
    loading: false,
    title: "",
    content: "",
    category: "",
    editingId: null
  };

  componentDidMount() {
    this.getData();
  }

  render() {
  const { data, loading, title, content, category, editingId } = this.state;
  return (
    <div>
      {!editingId ? (
        <form onSubmit={this.handleFormSubmit}>
          <label
htmlFor="title">Title:</label>
<input
         type="text"
         name="title"
         value={title}
         onChange={this.handleInputChange}
       />
<label htmlFor="content">Content:</label>
<input
         type="text"
         name="content"
         value={content}
         onChange={this.handleInputChange}
       />
<label htmlFor="category">Category:</label>
<input
         type="text"
         name="category"
         value={category}
         onChange={this.handleInputChange}
       />
<button type="submit" style={{ boxShadow: "none", backgroundColor: "#05FF13"}}>{editingId ? 'Update' : 'Create'} Note</button>
</form>
) : (
<div>
<h2>Edit Note</h2>
<form onSubmit={this.handleFormSubmit}>
<label htmlFor="title">Title:</label>
<input
             type="text"
             name="title"
             value={title}
             onChange={this.handleInputChange}
           />
<label htmlFor="content">Content:</label>
<input
             type="text"
             name="content"
             value={content}
             onChange={this.handleInputChange}
           />
<label htmlFor="category">Category:</label>
<input
             type="text"
             name="category"
             value={category}
             onChange={this.handleInputChange}
           />
<button type="submit">Update Note</button>
<button onClick={() => this.setState({ editingId: null, title: '', content: '', category: '' })}>Cancel</button>
</form>
</div>
)}
{loading ? (
<p>Loading...</p>
) : (
<table style={{ width: "100%", textAlign: "left" , border: "1px solid #000"}}>
<thead>
<tr>
<th>Title</th>
<th>Content</th>
<th>Category</th>
<th>Actions</th>
</tr>
</thead>
<tbody >
{data.map(item => (
<tr style={{ border: "1px solid #000" }} key={item.id}>
<td>{item.title}</td>
<td>{item.content}</td>
<td>{item.category}</td>
<td>
<button onClick={() => this.handleEditClick(item.id, item.title, item.content, item.category)}>Edit</button>
<button onClick={() => this.deleteData(item.id)}>Delete</button>
</td>
</tr>
))}
</tbody>
</table>
)}

</div>
);
}
}

export default Crud;
