import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [apiData, setApiData] = useState([]);
  const [newNote, setNewNote] = useState({ content: '', category: '' });
  const [updateNote, setUpdateNote] = useState({ id: '', content: '', category: '' });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:8000/api/notes/');
      setApiData(res.data.notes);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCreateNote = async () => {
    try {
      const res = await axios.post('http://127.0.0.1:8000/api/notes/', newNote);
      setMessage('Note created successfully');
      fetchNotes();
    } catch (error) {
      console.error(error);
      setMessage('Error creating note');
    }
  };

  const handleUpdateNote = async () => {
    try {
      const res = await axios.put(`http://127.0.0.1:8000/api/notes/${updateNote.id}/`, updateNote);
      setMessage('Note updated successfully');
      fetchNotes();
    } catch (error) {
      console.error(error);
      setMessage('Error updating note');
    }
  };

  const handleDeleteNote = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/notes/${id}/`);
      setMessage('Note deleted successfully');
      fetchNotes();
    } catch (error) {
      console.error(error);
      setMessage('Error deleting note');
    }
  };
return (
<div className="App">
<h1>React CRUD with Django REST API</h1>
<h2>Create Note</h2>
<input type="text" placeholder="Content" value={newNote.content} onChange={e => setNewNote({ ...newNote, content: e.target.value })} />
<input type="text" placeholder="Category" value={newNote.category} onChange={e => setNewNote({ ...newNote, category: e.target.value })} />
<button onClick={handleCreateNote}>Create Note</button>
<br />
<br />
<h2>Notes</h2>
{loading ? (
<div>Loading...</div>
) : error ? (
<div>Error loading notes</div>
) : (
<table>
<thead>
<tr>
<th>ID</th>
<th>Content</th>
<th>Category</th>
<th>Actions</th>
</tr>
</thead>
<tbody>
{apiData.map(note => (
<tr key={note.id}>
<td>{note.id}</td>
<td>{note.content}</td>
<td>{note.category}</td>
<td>
<button onClick={() => handleEditClick(note)}>Edit</button>
<button onClick={() => handleDeleteClick(note.id)}>Delete</button>
</td>
</tr>
))}
</tbody>
</table>
)}
{editing && renderUpdateForm()}
</div>
);
};

export default App;
