import React, { Component } from 'react';
import axios from 'axios';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      errorMessage: '',
    };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value,
    });
  }

  handleFormSubmit(event) {
    event.preventDefault();
    const { username, password } = this.state;
    axios
      .post('http://localhost:8000/authapp/api/notes/', { username, password })
      .then((response) => {
        if (response.status === 200) {
          // If the response is successful, set the user's session token in local storage or cookies
          localStorage.setItem('token', response.data.token);
          this.props.history.push('/dashboard');
        } else {
          // If the response is not successful, display an error message to the user
          this.setState({ errorMessage: response.data.message });
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }

  render() {
    return (
      <div>
        <h3>Login</h3>
        {this.state.errorMessage && <div className="error">{this.state.errorMessage}</div>}
        <form onSubmit={this.handleFormSubmit}>
          <label>
            Username:
            <input type="text" name="username" value={this.state.username} onChange={this.handleInputChange} />
          </label>
          <br />
          <label>
            Password:
            <input type="password" name="password" value={this.state.password} onChange={this.handleInputChange} />
          </label>
          <br />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default Login;
