import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const [authorId, setAuthorId] = useState(0);
  const [countLikes, setCountLikes] = useState(0);
  const [likedBy, setLikedBy] = useState(0);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
        setLikedBy(blogsWithLiked.liked_by);
        setCountLikes(blogsWithLiked);
        setAuthorId(blogsWithLiked.author);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  console.log("likedBy ", likedBy);

  // const handleLikeClick = async (blogId) => {
  //   try {
  //     const blogToUpdate = blogs.find((blog) => blog.id === blogId);
  //     const newLikedState = !blogToUpdate.liked;
  //     const newLikes = newLikedState
  //       ? blogToUpdate.likes + 1
  //       : blogToUpdate.likes - 1;

  //     const updatedBlogs = blogs.map((blog) =>
  //       blog.id === blogId
  //         ? { ...blog, liked: newLikedState, likes: newLikes }
  //         : blog
  //     );
  //     setBlogs(updatedBlogs);

  //     // Update liked_by state based on new liked state
  //     const newLikedByState = newLikedState
  //       ? [...blogToUpdate.liked_by, authorId]
  //       : blogToUpdate.liked_by.filter((id) => id !== authorId);
  //     setLikedBy(newLikedByState);

  //     // Update likes count
  //     setCountLikes(newLikes);

  //     // Call API to update like state
  //     await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
  //       method: "PUT",
  //       headers: {
  //         Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify({
  //         liked: newLikedState,
  //         likes: newLikes,
  //       }),
  //     });
  //   } catch (error) {
  //     console.error(error);
  //     setError("Failed to update like state.");
  //   }
  // };
  const handleLikeClick = async (blogId) => {
    try {
      const blogToUpdate = blogs.find((blog) => blog.id === blogId);
      const newLikedState = !blogToUpdate.liked;
      let newLikes = blogToUpdate.likes;
      let newLikedByState = [...blogToUpdate.liked_by];

      if (newLikedState) {
        newLikes += 1;
        newLikedByState.push(authorId);
      } else {
        newLikes -= 1;
        newLikedByState = newLikedByState.filter((id) => id !== authorId);
      }

      const updatedBlogs = blogs.map((blog) =>
        blog.id === blogId
          ? { ...blog, liked: newLikedState, likes: newLikes, liked_by: newLikedByState }
          : blog
      );
      setBlogs(updatedBlogs);

      // Update likes count
      setCountLikes(newLikes);

      // Call API to update like state
      await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          liked: newLikedState,
          likes: newLikes,
          liked_by: newLikedByState
        }),
      });
    } catch (error) {
      console.error(error);
      setError("Failed to update like state.");
    }
  };

  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog.id}>
          <IconButton onClick={() => handleLikeClick(blog.id)}>

            <span>{blog.likes}</span>
            <FavoriteIcon color={blog.liked_by.includes(blog.author) ? "error" : "inherit"} />

          </IconButton>
        </div>
      ))}
    </div>
  );
}
