import React, { useState } from 'react';
import {Link} from "react-router-dom";

function Update() {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState('');

  const [created_at, setCreatedAt] = useState("");
  const [updatedBlogImage, setUpdatedBlogImage] = useState(null)

  const handleUpdate = () => {
    const blogToUpdate = blogs.find(blog => blog.id === selectedBlogId);

    const updatedBlog = {
      id: selectedBlogId,
      image: updatedBlogImage, // use updated image if provided, otherwise null
    }

    const formData = new FormData();
    if (updatedBlog.image) {
      formData.append('image', updatedBlog.image);
    } else {
      formData.append('image', null);
    }

    fetch(`http://localhost:8000/api/blog/update/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        const updatedBlogIndex = blogs.findIndex(blog => blog.id === data.id);
        const updatedBlogs = [...blogs];
        updatedBlogs[updatedBlogIndex] = data;
        setBlogs(updatedBlogs);
        setUpdatedBlogImage(null);
      })
      .catch(error => console.error(error))
  }


  return (
    <div>
      {selectedBlogId !== '' && (
        <div>
          <div>
            <label htmlFor="blog-image">Image:</label>
            <input
              type="file"
              id="updatedBlogImage"
              name="updatedBlogImage"
              onChange={event => {
                if (event.target.files.length > 0) {
                  setUpdatedBlogImage(event.target.files[0]);
                } else {
                  setUpdatedBlogImage(null);
                }
              }}
            />
          </div>

          <button onClick={handleUpdate}>Update</button>
          <button onClick={() => setSelectedBlogId('')}>Cancel</button>
        </div>
      )}

      <button onClick={() => setSelectedBlogId(blogs.id)}>Edit</button>
    </div>
  );
}

export default Update;
