import React, { useState, useEffect } from 'react'

function BlogList() {
  const [blogs, setBlogs] = useState([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetch('http://localhost:8000/api/blog/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      setBlogs(data)
      setLoading(false)
    })
    .catch(error => console.error(error))
  }, [])

  const handleCreate = (newBlog) => {
    fetch('http://localhost:8000/api/blog/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(newBlog)
    })
    .then(response => response.json())
    .then(data => {
      setBlogs([...blogs, data])
    })
    .catch(error => console.error(error))
  }

  const handleUpdate = (updatedBlog) => {
    fetch(`http://localhost:8000/api/blog/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(updatedBlog)
    })
    .then(response => response.json())
    .then(data => {
      const index = blogs.findIndex(blog => blog.id === data.id)
      const newBlogs = [...blogs]
      newBlogs[index] = data
      setBlogs(newBlogs)
    })
    .catch(error => console.error(error))
  }

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      fetch(`/api/blog/${id}/`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        }
      })
      .then(response => {
        if (response.ok) {
          setBlogs(blogs.filter(blog => blog.id !== id))
        } else {
          throw new Error('Delete failed')
        }
      })
      .catch(error => {
        console.error(error)
      })
    }
  }

  if (loading) {
    return <p>Loading...</p>
  }

  return (
    <div>
      <h1>Blog List</h1>
      <ul>
        {blogs.map(blog => (
          <li key={blog.id}>
            <h2>{blog.title}</h2>
            <p>{blog.content}</p>
            <p>Author: {blog.authorName}</p>
            <button onClick={() => handleDelete(blog.id)}>Delete</button>
            <button onClick={() => handleUpdate({ id: blog.id, title: 'New Title', content: 'New Content', authorName: blog.authorName })}>Update</button>
          </li>
        ))}
      </ul>
      <button onClick={() => handleCreate({ title: 'New Blog', content: 'New Content', authorName: 'Anonymous' })}>Create</button>
    </div>
  )
}

export default BlogList
