import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
        setApiLikes(blog)
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLike = async (blogId) => {
    // Find the blog post to update
    const blog = blogs.find((blog) => blog.id === blogId);

    // Calculate the new like count and liked state
    const newLikes = blog.liked ? blog.likes - 1 : blog.likes + 1;
    const newLiked = !blog.liked;






    // Update the local state immediately
    const updatedBlogs = blogs.map((blog) => {
      if (blog.id === blogId) {
        return {
          ...blog,
          liked: newLiked,
          likes: newLikes,
        };
      }
      return blog;
    });
    setBlogs(updatedBlogs);

    // Call API to update like state
    await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        author_id: blog.author_id,
        likes: newLikes,
        liked: newLiked,
      }),
    });
  };

  const handleLiked = async (blogId) => {
    // Find the blog post to update
    const bloged = blogs.find((blog) => blog.id === blogId);

    // Calculate the new like count and liked state
    const newLikess = bloged.liked ? bloged.likes + 1 : bloged.likes - 1;
    const newLikedd = false;

    // Update the local state immediately
    const updatedBlogs = blogs.map((bloged) => {
      if (bloged.id === blogId) {
        return {
          ...bloged,
          liked: newLikedd,
          likes: newLikess,
        };
      }
      return bloged;
    });
    setBlogs(updatedBlogs);

    // Call API to update like state
    await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        author_id: bloged.author_id,
        likes: newLikess,
        liked: newLikedd,
      }),
    });
  };


  return (
    <div>
      {error && <p>{error}</p>}
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p>Likes: {blog.likes}</p>
            <p>liked by {blog.liked_by}</p>

            {blog.liked_by.includes(blog.author_id) ? (
              <div>
                    <h4>you did like it</h4>
                <IconButton onClick={() => handleLiked(blog.id)}>
                  <FavoriteIcon color={blog.liked ? "primary" : "error"} />
                </IconButton>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </div>
            ) : (
              <div>
                <h4>you did not like it</h4>
                <IconButton onClick={() => handleLike(blog.id)}>
                  <FavoriteIcon color={blog.liked ? "error" : "primary"} />
                </IconButton>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
            }
