import React, { useState, useEffect } from "react";

export default function Likes () {
  const [blogs, setBlogs] = useState([]);
  const [likes, setLikes] = useState(0);
  const [likesCount, setLikesCount] = useState(0)
  const [countLikes, setCountLikes] = useState(0);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // Fetch the blogs from the API
    fetch("http://localhost:8000/api/blog/")
      .then((res) => res.json())
      .then((data) => {
        setBlogs(data);
      })
      .catch((err) => {
        console.error(err);
      });

    // Check if the user is logged in on page load
    const token = localStorage.getItem("accessToken");
    if (token) {
      fetch("http://localhost:8000/api/auth/user/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((res) => res.json())
        .then((data) => {
          setCurrentUser(data);
        })
        .catch((err) => {
          console.error(err);
        });
    }
  }, []);
  const handleLikeClick = async (blogId) => {
    // Find the index of the blog post in the blogs state array
    const index = blogs.findIndex((blog) => blog.id === blogId);

    // Get the current liked and likes values for the blog post
    const { liked, likes, liked_by } = blogs[index];

    // Check if the user has already liked the post
    const userLiked = liked_by.some(user => user.id === currentUser.id);

    // Define newLikedBy based on the userLiked condition
    let newLikedBy = [];
    if (userLiked) {
      // Remove the user's ID from the liked_by array and decrease the like count
      newLikedBy = liked_by.filter(user => user.id !== currentUser.id);
      setLikesCount(likesCount - 1);
    } else {
      // Add the user's ID to the liked_by array and increase the like count
      newLikedBy = [...liked_by, { id: currentUser.id, username: currentUser.username }];
      setLikesCount(likesCount + 1);
    }

    // Update the liked and likes values for the blog post
    const updatedBlogs = [...blogs];
    updatedBlogs[index] = {
      ...blogs[index],
      liked: !liked,
      likes: newLikedBy.length,
      liked_by: newLikedBy,
    };
    setBlogs(updatedBlogs);

    // Update the API with the new liked state and like count
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${blogId}/`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            liked: !liked,
            likes: newLikedBy.length,
            liked_by: newLikedBy,
          }),
        }
      );

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
    {/* Render the blogs */}
    {blogs.map((blog) => (
    <div key={blog.id}>
    <h2>{blog.title}</h2>
    <p>{blog.content}</p>
    <p>Likes: {blog.likes}</p>
    {/ Render the like button if the user is logged in */}
    {currentUser && (
    <button onClick={() => handleLikeClick(blog.id)}>
    {blog.liked ? "Unlike" : "Like"}
    </button>
    )}
    </div>
    ))}
    </div>
    );
    }
