import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [liked, setLiked] = useState(false);
  const [likes, setLikes] = useState(0);
  const [blogs, setBlogs] = useState();
  const [likedby, setLikedBy] = useState(0);
  const [countLikes, setCountLikes] = useState(0);
  const [authorId, setAuthorId] = useState(0);
  const [error, setError] = useState();


  const handleLikeClick = () => {
    if (liked) {
      // User has already liked the post, so remove like
      setLiked(false);
      setLikes((prevLikes) => prevLikes - 1);
    } else {
      // User has not liked the post, so add like
      setLiked(true);
      setLikes((prevLikes) => prevLikes + 1);
    }
  };

    useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
        setLikedBy(blogsWithLiked.liked_by);
        setCountLikes(blogsWithLiked);
        setAuthorId(blogsWithLiked.author);
        console.log("likes --->", blogsWithLiked);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };
    fetchBlogs();
  }, []);

  return (
    <div>

      <div>
          <h2>Blogs</h2>
       <div className="blogs">
         {blogs.map((blog) => (
        <div key={blog.id}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p> {blog.liked_by}</p>
            <p>Likes: {blog.likes}</p>
{blog.liked_by}
      </div>
         ))}

    </div>
    </div>
      <IconButton onClick={handleLikeClick}>
        <FavoriteIcon color={liked ? "error" : "inherit"} />
        <span>{likes}</span>
      </IconButton>
    </div>
  );
}
