import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

const Likes = () => {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState("");
  const [likeCount, setLikeCount] = useState(0);
  const [liked, setLiked] = useState(false);

  const handleBlogClick = (blogId) => {
    setSelectedBlogId(blogId);
    const selectedBlog = blogs.find((blog) => blog.id === blogId);
    setLikeCount(selectedBlog.likes);
    setLiked(selectedBlog.liked);
  };

  const handleLikeClick = () => {
    fetch(`http://localhost:8000/api/blog/likes/update/${selectedBlogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
      },
      body: JSON.stringify({
        liked: !liked,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        const index = blogs.findIndex((blog) => blog.id === data.id);
        const newBlogs = [...blogs];
        newBlogs[index] = data;

        setBlogs(newBlogs);
        setLiked(data.liked);
        setLikeCount(data.likes);
      })
      .catch((error) => console.error(error));
  };

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBlogs();
  }, []);

  return (
    <div>
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id} onClick={() => handleBlogClick(blog.id)}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p>Likes: {blog.likes}</p>
          </div>
        ))}
      </div>

      {selectedBlogId && (
        <div className="likes">
          <h3>
            {blogs.find((blog) => blog.id === selectedBlogId).title} - Likes:{" "}
            {likeCount}
          </h3>
          <IconButton onClick={() => {
            setLikeCount(likeCount + (liked ? -1 : 1));
            setLiked(!liked);
          }}>
            <FavoriteIcon color={liked ? "error" : "primary"} />
          </IconButton>
          <IconButton onClick={handleLikeClick}>
            <ShareIcon />
          </IconButton>
        </div>
      )}
    </div>
  );
};

export default Likes;
