import React, { useState } from "react";

function BlogListLikes({ blogs }) {
  const [likes, setLikes] = useState({});

  const handleLike = (blogId) => {
    const url = `/api/blogs/${blogId}/like/`;

    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    })
      .then((response) => response.json())
      .then((data) => setLikes((prevLikes) => ({ ...prevLikes, [blogId]: data.likes })))
      .catch((error) => console.error(error));
  };

  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <button onClick={() => handleLike(blog.id)}>Like</button>
          <p>Likes: {likes[blog.id] || blog.likes}</p>
        </div>
      ))}
    </div>
  );
}

export default BlogListLikes;
