import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

export default function Likes() {


  return (
    <div>
      {error && <p>{error}</p>}
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p>Likes: {blog.likes}</p>

            {blog.liked_by.includes(blog.author) ? (
              <div>
                    <h4>you did like it</h4>
                <IconButton onClick={() => handleLiked(blog.id)}>
                  <FavoriteIcon color={blog.liked ? "primary" : "error"} />
                </IconButton>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </div>
            ) : (
              <div>
                <h4>you did not like it</h4>
                <IconButton onClick={() => handleLike(blog.id)}>
                  <FavoriteIcon color={blog.liked ? "error" : "primary"} />
                </IconButton>
                <IconButton>
                  <ShareIcon />
                </IconButton>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
            }
