import React, { useEffect, useState } from 'react';
import {Link} from "react-router-dom";
import axios from "axios";

function Update() {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState('');

  const [created_at, setCreatedAt] = useState("");
  const [updatedBlogImage, setUpdatedBlogImage] = useState(null)

  const handleUpdate = () => {
    const blogToUpdate = blogs.find(blog => blog.id === selectedBlogId);

    const updatedBlog = {
      id: selectedBlogId,
      image: updatedBlogImage, // use updated image if provided, otherwise null
    }

    const formData = new FormData();
    if (updatedBlog.image) {
      formData.append('image', updatedBlog.image);
    } else {
      formData.append('image', null);
    }

    fetch(`http://localhost:8000/api/blog/update/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        const updatedBlogIndex = blogs.findIndex(blog => blog.id === data.id);
        const updatedBlogs = [...blogs];
        updatedBlogs[updatedBlogIndex] = data;
        setBlogs(updatedBlogs);
        setUpdatedBlogImage(null);
      })
      .catch(error => console.error(error))
  }

  useEffect(()=>{
    axios.get("http://localhost:8000/api/blog/list/", {
      'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
    }).then((response)=>{
      console.log(response.data);           
