  const handleUpdate = () => {
    const blogToUpdate = blogs.find(blog => blog.id === selectedBlogId);

    const updatedBlog = {
      id: selectedBlogId,
      title: updatedBlogTitle === undefined ? blogToUpdate.title : updatedBlogTitle,
      content: updatedBlogContent === undefined ? blogToUpdate.content : updatedBlogContent,
      authorName: updatedBlogAuthorName === undefined ? blogToUpdate.authorName : updatedBlogAuthorName,
      image: updatedBlogImage || blogToUpdate.image, // use updated image if provided, otherwise use the previous image
    }

    const formData = new FormData();
    formData.append('title', updatedBlog.title);
    formData.append('content', updatedBlog.content);
    formData.append('authorName', updatedBlog.authorName);
    formData.append('image', updatedBlog.image);

    fetch(`http://localhost:8000/api/blog/update/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        const updatedBlogIndex = blogs.findIndex(blog => blog.id === data.id);
        const updatedBlogs = [...blogs];
        updatedBlogs[updatedBlogIndex] = data;
        setBlogs(updatedBlogs);
        setSelectedBlogId('');
        setUpdatedBlogTitle(undefined);
        setUpdatedBlogContent(undefined);
        setUpdatedBlogAuthorName(undefined);
        setUpdatedBlogImage(null);
        setImageUrl(null);
      })
      .catch(error => console.error(error))
  }

  // Check if there is a previously saved image for the record and prepopulate the image upload field
  useEffect(() => {
    if (selectedBlogId !== '') {
      setImageUrl(blogs.find(blog => blog.id === selectedBlogId).image)
    }
  }, [selectedBlogId])

  return (
    <div>

