import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // added state for redirecting

  const [userValidate, setUserValidate] = useState('');
  const [pwdValidate, setPwdValidate] = useState('');

  const handleUsernameChange = (event) => {
    if(event.target.value != 5) {
      setUserValidate("Username must be at least of 5 Character!");
      setUsername(event.target.value);
    } else{
      setUserValidate("");
      setUsername();
    }
  }
  const handlePasswordChange = (event) => {
    (event.target.value != 5) ?
      setPwdValidate("Password must be at least of 5 Character!"):
      setPassword(event.target.value);


  };
  const navigate = useNavigate();


  const handleSubmit = event => {
    event.preventDefault();
    fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      })
      .then(response => {
        if (response.ok) {
          return response.json()
        } else {
          throw new Error('Login failed')
        }
      })
      .then(data => {
        // Save the refresh and access tokens in local storage or cookies
        localStorage.setItem('refreshToken', data.refresh)
        localStorage.setItem('accessToken', data.access)
        setLoggedIn(true);
        navigate("/blog");
      })
      .catch(error => {
        console.error(error)
      })
    }

  return (

    <form onSubmit={handleSubmit}>
     <h2>Login</h2>
      <div>
        <label htmlFor="username">Username:</label> <br></br>
        {userValidate && <span>{userValidate}</span>}
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label><br></br>
        {pwdValidate && <span>{pwdValidate}</span>}
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      <button type="submit">Log in</button>

      <h4>Donot have an account? Create an account?</h4> 
      <Link to="/signup">Signup </Link>
    </form>
  );
}

export default Login;
