import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  liked: {
    color: theme.palette.error.main,
  },
}));

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const classes = useStyles();

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLikeClick = async (blogId) => {
    const blogToUpdate = blogs.find((blog) => blog.id === blogId);

    if (blogToUpdate.liked) {
      // User has already liked the blog, so remove like
      blogToUpdate.liked = false;
      blogToUpdate.likes -= 1;
    } else {
      // User has not liked the blog, so add like
      blogToUpdate.liked = true;
      blogToUpdate.likes += 1;
    }

    // Update the API with the new liked state and like count
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${blogId}/`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            liked: blogToUpdate.liked,
            likes: blogToUpdate.likes,
          }),
        }
      );

      const data = await response.json();
      setBlogs((prevBlogs) =>
        prevBlogs.map((blog) => (blog.id === data.id ? data : blog))
      );
    } catch (error) {
      console.error(error);
      setError("Failed to update like state.");
    }
  };

  return (
    <div>
      {error && <div>{error}</div>}
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <span>{blog.likes}</span>
          <IconButton onClick={() => handleLikeClick(blog.id)}>
            {blog.liked ? (
              <FavoriteIcon  />
            ) : (
              <FavoriteBorderIcon className={classes.liked}/>
            )}
          </IconButton>
        </div>
      ))}
    </div>
  );
}
