import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [liked, setLiked] = useState(false);
  const [likes, setLikes] = useState(0);
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const [authorId,setAuthorId] = useState(0);
  const [countLikes, setCountLikes] = useState(0);
  const [likedBy,setLikedBy] = useState(0);
  const [newAddLikes,setNewAddLikes] = useState(0);
  const [newSubLikes,setNewSubLikes] = useState(0);
  const [likedByAuthor, setLikedByAuthor] = useState(false);
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
        setLikedBy(blogsWithLiked.liked_by);
        setCountLikes(blogsWithLiked.likes);
        setAuthorId(blogsWithLiked.author);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  // const handleLikeClick = () => {
  //   if (liked) {
  //     // User has already liked the post, so remove like
  //     setLiked(false);
  //     const newLikes = countLikes - 1;
  //     setNewAddLikes(newLikes);
  //     setLikes(newLikes);
  //   } else {
  //     // User has not liked the post, so add like
  //     setLiked(true);
  //     const newLikes = countLikes + 1;
  //     setNewSubLikes(newLikes);
  //     setLikes(newLikes);
  //   }
  // };

  const handleLikeClick = async (blogId, currentLikes, likedByAuthor, setLikes, setLikedByAuthor) => {
    const accessToken = localStorage.getItem("accessToken");
    const response = await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        liked: !likedByAuthor,
        likes: currentLikes + (likedByAuthor ? -1 : 1),
        liked_by: !likedByAuthor,
      }),
    });

    if (response.ok) {
      // Update the state to reflect the new like state and count
      setLikes(currentLikes + (likedByAuthor ? -1 : 1));
      setLikedByAuthor(!likedByAuthor);
    } else {
      console.error(response.statusText);
      setError("Failed to update like state.");
    }
  };

  // Usage example:






  return (
    <div>

<div className="blogs">
  {blogs &&
    blogs.map((blog) => (
      <div key={blog.id}>
        <h3>{blog.title}</h3>
        <p>{blog.content}</p>
        <p>Likes: {blog.likes}</p>
        <p>Liked by: {blog.liked_by.join(", ")}</p>
        <p>
          Author: {blog.author.first_name} {blog.author.last_name}
        </p>
        <p> {blog.liked_by}</p>
        <p>Likes: {blog.likes}</p>
        <p>liked by: {blog.liked_by} </p>
        <IconButton onClick={handleLikeClick}>
        <FavoriteIcon color={liked ? "error" : "inherit"} />
        <span>{likes}</span>
      </IconButton>

      </div>
    ))}
</div>



    </div>
  );
}
