import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import CommentIcon from "@mui/icons-material/Comment";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Popover from '@mui/material/Popover';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);
  const [shareUrl, setShareUrl] = useState("");
  const [copied, setCopied] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);
  const [copiedStates, setCopiedStates] = useState([]); // Initialize an empty array
  const [likes, setLikes] = useState([]);
  const [liked, setLiked] = useState([]);

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    setCopied(true);
    setCopySuccess(true);
  };

  const cancelCopy = () => {
    setCopySuccess(false);
    setCopied(false);
    setCopySuccess(false);
  };

  useEffect(() => {
    const fetchLikes = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/likes/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        const blogLikes = data.map((blog) => {
          return {
            id: blog.id,
            likes: blog.likes,
            liked_state: blog.liked_state,
            title: blog.title,
            image: blog.image,
            content: blog.content,
            authorName: blog.authorName,
            created_at: blog.created_at,
            updated_at: blog.updated_at,

          };
        });

        setBlogs(blogLikes);
        setLikes(blogLikes.map((blog) => blog.likes));
        setLiked(blogLikes.map((blog) => blog.liked_state));
        setCopiedStates(blogLikes.map(() => false)); // Initialize an array of false values for each blog post
      } catch (error) {
        console.error(error);
      }
    };

    fetchLikes();
  }, []);

  return (
    <div>
      {blogs.map((blog, index) => (
        <div key={blog.id}>
          <IconButton
            aria-describedby={`popover-${index}`}
            onClick={(event) => {
              setAnchorEl(event.currentTarget);
              setShareUrl(`${window.location.origin}/blog/${blog.id}`);
            }}
          >
            <ShareIcon color="action" style={{ width: "30px", height: "30px" }} />
          </IconButton>

          <Popover
            open={Boolean(anchorEl)}
            anchorEl={anchorEl}
            onClose={() => {
              setAnchorEl(null);
              setCopied(false);
            }}
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "center",
            }}
            transformOrigin={{
              vertical: "top",
              horizontal: "center",
            }}
          >
            {copied && (
              <Typography sx={{ p: 2 }}>Link copied to clipboard!</Typography>
            )}
            <Typography sx={{ p: 2 }}>{shareUrl}</Typography>
            <Button
              variant="contained"
              disabled={copiedStates[index]}
              onClick={() => {
                copyToClipboard(shareUrl);

                let newCopiedStates = [...copiedStates]; // create a new array to update copiedStates
                newCopiedStates[index] = true; // set the copied state of the current blog post to true
                setCopiedStates(newCopiedStates); // update the copiedStates array with the new value
                }}
                >
                {copiedStates[index] ? "Copied" : "Copy"}
                </Button>
                <Button variant="contained" onClick={() => setAnchorEl(null)}>
                Cancel
                </Button>
                </Popover>
                </div>
                ))}
                </div>
                );
                }

                export default Likes;
