import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

function Likes({ postId }) {
  const [likes, setLikes] = useState(0);
  const [liked, setLiked] = useState(false);

  // Function to fetch likes from the API
  const fetchLikes = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/blog/likes/', {
        headers: {
          'Authorization': 'Bearer <token>', // Replace <token> with the actual token
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      const postLikes = data.find(post => post.id === postId).likes;
      setLikes(postLikes);
    } catch (error) {
      console.log(error);
    }
  };

  // Function to update likes on the API
  const updateLikes = async () => {
    try {
      const response = await fetch(`http://localhost:8000/api/blog/likes/update/${postId}/`, {
        method: 'PUT',
        headers: {
          'Authorization': 'Bearer <token>', // Replace <token> with the actual token
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          author_id: 1, // Replace with the actual author id
          username: 'dilli', // Replace with the actual username
          likes: liked ? likes - 1 : likes + 1,
          liked: !liked
        })
      });
      const data = await response.json();
      setLikes(data.likes);
      setLiked(data.liked);
    } catch (error) {
      console.log(error);
    }
  };

  // Fetch likes from the API on mount
  useEffect(() => {
    fetchLikes();
  }, []);

  // Render the like button with the current likes and handle the click event
  return (
    <div>
      <button onClick={updateLikes}>{liked ? 'Unlike' : 'Like'}</button>
      <span>{likes} likes</span>
    </div>
  );
}

export default Likes;
