import React, { useState, Component, useEffect } from 'react';
import Dashboard from './dashboard';
import axios from "axios";

const csrftoken = getCookie('csrftoken'); 
function getCookie(name) {
    const cookies = document.cookie.split(';');
    for (let i = 0; i < cookies.length; i++) {
      const cookie = cookies[i].trim();
      if (cookie.startsWith(`${name}=`)) {
        return cookie.substring(name.length + 1);
      }
    }
    return null;
  }



function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // added state for redirecting

  const handleUsernameChange = event => setUsername(event.target.value);
  const handlePasswordChange = event => setPassword(event.target.value);


  const handleSubmit = event => {
    event.preventDefault();
    // get CSRF token from cookie
    fetch('/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          username: 'example_user',
          password: 'example_password'
        })
      })
      .then(response => {
        if (response.ok) {
          return response.json()
          setLoggedIn(true); // set loggedIn to true after successful login
        } else {
          throw new Error('Login failed')
        }
      })
      .then(data => {
        // Save the refresh and access tokens in local storage or cookies
        localStorage.setItem('refreshToken', data.refresh)
        localStorage.setItem('accessToken', data.access)
      })
      .catch(error => {
        console.error(error)
      })
  };

  if (loggedIn) { // redirect to Dashboard if loggedIn is true
    return <Dashboard/>;
  }


  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      <button type="submit">Log in</button>
    </form>
  );
}

export default Login;
