import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';


export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const [countLikes, setCountLikes] = useState(0);
  const [likedBy, setLikedBy] = useState(0);
  const [authorId, setAuthorId] = useState(null);

  console.log(authorId);
  console.log(likedBy);


  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);

        setCountLikes(blogsWithLiked[0].likes);
        setLikedBy(blogsWithLiked.liked_by);

        setAuthorId(blogsWithLiked.author);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLikeClick = (blogId) => {
    const index = blogs.findIndex((blog) => blog.id === blogId);
    const blogToUpdate = { ...blogs[index] };

    if (blogToUpdate.liked || blogToUpdate.author == authorId) {
      // If the blog post is already liked or the author is not the current user,
      // don't decrease the like count
      if (blogToUpdate.likes > 0) {
        blogToUpdate.likes--;
        setCountLikes(countLikes - 1);
      }
    } else {
      // If the blog post is not liked and the author is the current user, increase the like count by 1
      blogToUpdate.likes++;
      setCountLikes(countLikes + 1);
    }

    // 
    // Toggle the liked state for the blog post
    blogToUpdate.liked = !blogToUpdate.liked;

    // Update the state with the new liked state and like count
    const newBlogs = [...blogs];
    newBlogs[index] = blogToUpdate;
    setBlogs(newBlogs);

    // Update the API with the new liked state and like count
    fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({

          liked: blogToUpdate.liked,
          likes: blogToUpdate.likes,


      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to update like state.');
        }
        return response.json();
      })
      .then((data) => console.log(data))
      .catch((error) => {
        console.error(error);
        setError("Failed to update like state.");
      });
  };

  return (
    <div>

      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          liked by : {blog.liked_by} <br></br>
          likes: <span>{blog.likes}</span>

          {/* {blog.liked_by == blog.author ? 
            <div>
                        <IconButton onClick={() => handleLikeClick(blog.id)}>

                        <FavoriteIcon color="error" />
</IconButton>

               </div>
            : <div>          <IconButton onClick={() => handleLikeClick(blog.id)}>

            <FavoriteIcon color="error" />
</IconButton>
</div>} */}

 { blog.liked_by == blog.author ? 
 <div>  </div>: <div> </div> }

          <IconButton>
            <ShareIcon />
          </IconButton>
        </div>
      ))}

    </div>
  );
}
