    const [userErrorMessage,setUserErrorMessage] = useState("");
    const [usernameExistsError, setUsernameExistsError] = useState(null);
    const [errorMsg, setErrorMsg] = useState("");
    const [errorMessage, setErrorMessage] = useState('');
    const [emailExist,setEmailExist] = useState('');
    const navigate = useNavigate();


    const handleSubmit = async (event) => {
      event.preventDefault();
      // added check for empty input fields
      if (!username || !password || !email || !confirmPassword) {
        setErrorMsg("Please Fill the Input!");
        return;
      }

      try {
        const response = await fetch("http://localhost:8000/api/register/", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ username, email, password }),
        });

        const data = await response.json();

        if (response.ok) {
          setRegister(true);
          setMessage(
            "Successfully signed up! Please wait while you are redirected to the login page..."
          );
          setTimeout(() => {
            navigate("/login");
          }, 1500);
        } else if (response.status === 409) {
          setErrorMessage("USERNAME EXISTS");
          throw new Error("Username is already taken");
        } else {
          setApiError("Something went wrong!");
        }
      } catch (error) {
        console.error(error);
        setApiError("An error occurred while signing up. Please try again.");
      }
    };

    const handleUsernameChange = (event) => {

        setUsername(event.target.value);
    };

    const handleEmailChange = (event) => {
      const { value } = event.target;
      setEmail(value);

      // Email validation using regular expression
      const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
      setEmailValidation(!emailRegex.test(value) ? 'Please enter a valid email address.' : '');
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
        const passwordRegex = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
        setPasswordValidation(!passwordRegex.test(event.target.value) ? "Password must contain at least 8 characters including 1 uppercase, 1 lowercase, 1 digit and 1 special character" : "");
    };

    const handleConfirmPasswordChange = (event) => {
        setConfirmPassword(event.target.value);
        if (!event.target.value) {
          setConfirmPwdMsg("");
        } else if (event.target.value !== password) {
          setConfirmPwdMsg("Passwords do not match");
        } else {
          setConfirmPwdMsg("");
        }
    };

  return (
    <form onSubmit={handleSubmit} >

  <ThemeProvider theme={theme}>
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={12}>
              <TextField
                autoComplete="given-name"
                name="firstName"
                required
                fullWidth
                id="username"
                label="Username"
                autoFocus
                value={username} onChange={handleUsernameChange} placeholder="Enter Username"
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                required
                fullWidth
                label="Email Address"
                name="email"
                autoComplete="email"
                type="email" id="email" value={email} onChange={handleEmailChange} placeholder="Enter Email"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="new-password"
                value={password} onChange={handlePasswordChange} placeholder="Enter Password"
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                required
                fullWidth
                name="confirm-password"
                label="Confirm Password"
                type="password"
                id="confirm-password"
                autoComplete="new-password"
                value={confirmPassword} onChange={handleConfirmPasswordChange} placeholder="Enter Confirm Password"
              />
            </Grid>

          </Grid>

          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Sign Up
          </Button>
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Link to="/login" variant="body2">
                Already have an account? Sign in
              </Link>
            </Grid>
          </Grid>
        </Box>
      </Box>
      <Copyright sx={{ mt: 5 }} />
    </Container>
  </ThemeProvider>
);

    </form>

  );
}
