import React, { useState, useEffect } from 'react';

function Update() {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState('');
  const [updatedBlogTitle, setUpdatedBlogTitle] = useState(undefined);
  const [updatedBlogContent, setUpdatedBlogContent] = useState(undefined);
  const [updatedBlogAuthorName, setUpdatedBlogAuthorName] = useState(undefined);

  const [newBlogImage, setNewBlogImage] = useState(null);
  const [updatedBlogImage, setUpdatedBlogImage] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {
    // Fetch blogs data and update state
    fetch('http://localhost:8000/api/blog/', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
    })
      .then(response => response.json())
      .then(data => setBlogs(data))
      .catch(error => console.error(error))
  }, [])

  const handleUpdate = () => {
    const updatedBlog = {
      id: selectedBlogId,
      title: updatedBlogTitle === undefined ? blogs.find(blog => blog.id === selectedBlogId).title : updatedBlogTitle,
      content: updatedBlogContent === undefined ? blogs.find(blog => blog.id === selectedBlogId).content : updatedBlogContent,
      authorName: updatedBlogAuthorName === undefined ? blogs.find(blog => blog.id === selectedBlogId).authorName : updatedBlogAuthorName
    }
    const formData = new FormData();
    formData.append('title', updatedBlog.title);
    formData.append('content', updatedBlog.content);
    formData.append('authorName', updatedBlog.authorName);

    // Check if user has uploaded a new image, if not, use the previously saved image
    if (updatedBlogImage === null) {
      formData.append('image', blogs.find(blog => blog.id === selectedBlogId).image);
    } else {
      formData.append('image', updatedBlogImage);
    }

    fetch(`http://localhost:8000/api/blog/update/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        const index = blogs.findIndex(blog => blog.id === data.id)
        const newBlogs = [...blogs]
        newBlogs[index] = data
        setBlogs(newBlogs)
        setSelectedBlogId('')
        setUpdatedBlogTitle(undefined)
        setUpdatedBlogContent(undefined)
        setUpdatedBlogAuthorName(undefined)
        setUpdatedBlogImage(null)
        setImageUrl(null)
      })
      .catch(error => console.error(error))
  }

  // Check if there is a previously saved image for the record and prepopulate the image upload field
  useEffect(() => {
    if (selectedBlogId !== '') {
      setImageUrl(blogs.find(blog => blog.id === selectedBlogId).image)
    }
  }, [selectedBlogId])

  return (
    <div>
      {/* {selectedBlogId !== '' && (
        <div>
          <h2>Update Blog "{blogs.find(blog => blog.id === selectedBlogId).title}"</h2>

          <div>
            <label htmlFor="blog-title">Title:</label>
            <input type="text" id="blog-title" value={updatedBlogTitle !== undefined ? updatedBlogTitle : blogs.find(blog => blog.id === selectedBlogId).title} onChange={(e) => setUpdatedBlogTitle(e.target.value)} />


            <div>
        <label htmlFor="blog-content">Content:</label>
        <textarea id="blog-content" value={updatedBlogContent !== undefined ? updatedBlogContent : blogs.find(blog => blog.id === selectedBlogId).content} onChange={(e) => setUpdatedBlogContent(e.target.value)}></textarea>
      </div>

      <div>
        <label htmlFor="blog-author-name">Author Name:</label>
        <input type="text" id="blog-author-name" value={updatedBlogAuthorName !== undefined ? updatedBlogAuthorName : blogs.find(blog => blog.id === selectedBlogId).authorName} onChange={(e) => setUpdatedBlogAuthorName(e.target.value)} />
      </div>

      <div>
        <label htmlFor="blog-image">Image:</label>
        {imageUrl && <img src={imageUrl} alt="Blog" width="100" height="100" />}
        <input type="file" id="blog-image" accept="image/*" onChange={(e) => setUpdatedBlogImage(e.target.files[0])} />
      </div>

      <button onClick={handleUpdate}>Update</button>
      <button onClick={() => setSelectedBlogId('')}>Cancel</button>
    </div>
  )} */}

  {selectedBlogId === '' && (
    <div>
      <h2>Select a blog to update</h2>
      <ul>
        {blogs.map(blog => (
          <li key={blog.id}>
            <h3>{blog.title}</h3>
            <button onClick={() => setSelectedBlogId(blog.id)}>Update</button>
          </li>
        ))}
      </ul>
    </div>
  )}
</div>
  )
}

export default Update;
