import { useState, useEffect } from "react";

const Likes = () => {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState("");
  const [likeCount, setLikeCount] = useState(0);
  const [liked, setLiked] = useState(false);

  const handleBlogClick = (blogId) => {
    setSelectedBlogId(blogId);
    setLikeCount(blogs.find((blog) => blog.id === blogId).likes);
    setLiked(blogs.find((blog) => blog.id === blogId).liked);
  };

  const handleLikeClick = () => {
    setLikeCount(liked ? likeCount - 1 : likeCount + 1);
    setLiked(!liked);
  };

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBlogs();
  }, []);

  useEffect(() => {
    const handleLikeUpdate = async () => {
      if (selectedBlogId !== "") {
        try {
          const response = await fetch(
            `http://localhost:8000/api/blog/likes/update/${selectedBlogId}/`,
            {
              method: "PUT",
              headers: {
                Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
                "Content-Type": "application/json",
              },
              body: JSON.stringify({
                liked: liked,
              }),
            }
          );

          const data = await response.json();
          const index = blogs.findIndex((blog) => blog.id === data.id);
          const newBlogs = [...blogs];
          newBlogs[index] = data;
          setBlogs(newBlogs);
        } catch (error) {
          console.error(error);
        }
      }
    };

    handleLikeUpdate();
  }, [liked]);

  return (
    <div>
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id} onClick={() => handleBlogClick(blog.id)}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p>Likes: {blog.likes}</p>
          </div>
        ))}
      </div>
      {selectedBlogId && (
        <div className="likes">
          <h3>
            {blogs.find((blog) => blog.id === selectedBlogId).title} - Likes:{" "}
            {likeCount}
          </h3>

          {/* {blog.likes} 
            <IconButton aria-label="add to favorites" onClick={handleLikeClick}> 
          <FavoriteIcon />  */}

          <button onClick={handleLikeClick}>
            {liked ? "" : ""}
          </button>
        </div>
      )}
    </div>
  );
};

export default Likes;
