import React, { useState } from 'react';
import Blog from './blog';
import { Link, Navigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // added state for redirecting

  const [errorMsg, setErrorMsg] = useState('');
  const [authMsg, setAuthMsg] = useState('');
  const [apiErrorMsg,setApiErrorMsg] = useState('');

  const handleUsernameChange =(event) => {
    if (username.length < 5) {
      setUsrErrorMessage('Username should have at least 5 characters');
    } else if(username.length > 5){
      setUsrErrorMessage('');
    }
    setUsername(event.target.value)
  };

  const handlePasswordChange = (event) => {
    if(password.length < 8){
      setPwdErrorMessage('password should have at least 8 characters');
    } else if(password.length > 8){
      setPwdErrorMessage('');
    }
    setPassword(event.target.value);
  };

  const [pwdErrorMessage, setPwdErrorMessage] = useState('');
  const [usrErrorMessage,setUsrErrorMessage] = useState('');


  const handleSubmit = (event) => {
    event.preventDefault();



      fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      })
      .then(response => {
        if (response.ok) {
          return response.json();
        }

       else if (!username || !password) { // added check for empty input fields
          setErrorMsg("Please Fill the Fields!");

        } else if(username || password){
          setErrorMsg("");
          return;
        }

        else if (response.status == 400) {
          // Bad request error
          setApiErrorMsg("Invalid request. Please check your inputs and try again.")
          throw new Error('Invalid request. Please check your inputs and try again.');
        }
        else if (response.status == 401) {
          // Authentication error
          setApiErrorMsg('Invalid credentials. Please try again with valid username and password.');
          throw new Error('Invalid credentials. Please try again with valid username and password.');
        }
        else if (response.status == 403) {
          // Authorization error
          setApiErrorMsg('You do not have permission to access this resource.');
          throw new Error('You do not have permission to access this resource.');
        }
        else if (response.status == 404) {
          // Resource not found error
          setApiErrorMsg('The requested resource was not found on the server.');
          throw new Error('The requested resource was not found on the server.');
        }
        else if (response.status == 500) {
          setApiErrorMsg('Internal server error. Please try again later.');
          // Internal server error
          throw new Error('Internal server error. Please try again later.');
        }
        else {
          setApiErrorMsg('server maybe down');
          // Other error
          throw new Error('Login failed. Please try again later.');
        }
      })
      .then(data => {
        // Save the refresh and access tokens in local storage or cookies
        localStorage.setItem('refreshToken', data.refresh)
        localStorage.setItem('accessToken', data.access)
        setLoggedIn(true);
        // Redirect to dashboard or homepage
      })
      .catch(error => {
        console.error(error);
        setApiErrorMsg(error.message);
      })
    }

  if (loggedIn) { 
    // redirect to Dashboard if loggedIn is true
    return <Navigate to="/blog"/>
  }


  return (

    <form onSubmit={handleSubmit}>
     <h2>Login</h2>
      <div>
        <label htmlFor="username">Username:</label> <br></br>
        {usrErrorMessage} <br></br>
        {errorMsg} <br></br>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label><br></br>
        {pwdErrorMessage}<br></br>
        {errorMsg} <br></br>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      {apiErrorMsg} <br></br>
      <button type="submit">Log in</button>
    </form>
  );
}

export default Login;
