import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';


export default function Likes({ blogId, likes, likedBy }) {
  const [isLiked, setIsLiked] = useState(likedBy.includes(authorId));
  const [numLikes, setNumLikes] = useState(likes);
  const [likedByList, setLikedByList] = useState(likedBy);
  const [error, setError] = useState("");
  const [authorId,setAuthorId] = useState(1); // replace with the actual author ID
  const [countLikes, setCountLikes] = useState(0);

  useEffect(() => {
    const fetchBlogLikes = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/blog/likes/${blogId}/`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });
        const data = await response.json();
        setIsLiked(data.liked_by.includes(authorId));
        setNumLikes(data.likes);
        setLikedByList(data.liked_by);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog likes.");
      }
    };

    fetchBlogLikes();
  }, []);

  const handleLikeClick = async () => {
    try {
      let newLikedState = !isLiked;
      let newLikes = numLikes;
      let newLikedByState = likedByList;

      if (newLikedState) {
        newLikes += 1;
        newLikedByState = [...likedByList, authorId];
      } else {
        newLikes -= 1;
        newLikedByState = likedByList.filter((id) => id !== authorId);
      }

      setIsLiked(newLikedState);
      setNumLikes(newLikes);
      setLikedByList(newLikedByState);

      // Call API to update like state
      await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          author_id: authorId,
          liked: newLikedState,
          likes: newLikes,
          liked_by: newLikedByState,
        }),
      });
    } catch (error) {
      console.error(error);
      setError("Failed to update like state.");
    }
  };

  return (
    <div>
      <IconButton onClick={handleLikeClick}>
        {isLiked ? <FavoriteIcon color="error" /> : <FavoriteIcon />}
      </IconButton>
      <span>{numLikes}</span>
      <IconButton>
        <ShareIcon />
      </IconButton>
      {error && <p>{error}</p>}
    </div>
  );
}
