function Likes(props) {
  const [likedPost, setLikedPost] = useState(props.liked);
  const [Likeblogs, setLikeBlogs] = useState();
  const [blogId, setBlogIds] = useState();

  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const [authorId,setAuthorId] = useState(0);
  const [countLikes, setCountLikes] = useState(0);
  const [likedBy,setLikedBy] = useState(0);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        console.log(data);

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));
        setLikedPost(blogsWithLiked);

        setBlogs(blogsWithLiked);
        setLikedBy(blogsWithLiked.liked_by);
        setCountLikes(blogsWithLiked);
        setAuthorId(blogsWithLiked.author);
        // Store all the blog IDs in an array
        const blogIds = data.map((blog) => blog.id);
        setBlogIds(blogIds);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBlogs();
  }, []);

   console.log(blogId);

  const handleLikeClick = () => {
    const newLikedState = !likedPost;
    setLikedPost(newLikedState);

    const data = {
      author_id: props.author_id,
      blog_name: props.blog_name,
      likes: props.likes + (newLikedState ? 1 : -1),
      liked: newLikedState
    }




    fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return response.json();
    })
    .then(data => console.log(data))
    .catch(error => console.error(error));
  };

  return (
    <div>
      {error && <p>{error}</p>}
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p> {blog.liked_by}</p>
            <p>Likes: {blog.likes}</p>
liked/by{blog.liked_by} <br></br>
author id{blog.author_id}


<button onClick={handleLikeClick}>
        {setLikedPost ? 'Unlike' : 'Like'}
      </button>
          </div>
        ))}
      </div>
    </div>
  );
}
