import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLikeClick = async (blogId) => {
    // Find the index of the blog post in the blogs state array
    const index = blogs.findIndex((blog) => blog.id === blogId);

    // Get the current liked and likes values for the blog post
    const { liked, likes } = blogs[index];

    // Get the liked_by array for the blog post
    const likedBy = blogs[index].liked_by;

    // Check if the current user is the author of the blog post
    const isAuthor = likedBy.some((user) => user.id === localStorage.getItem("userId"));

    // If the current user is the author, do not allow them to like the post again
    if (isAuthor) {
      console.log("You cannot like your own post.");
      return;
    }

    // Update the liked and likes values for the blog post
    const updatedBlogs = [...blogs];
    updatedBlogs[index] = {
      ...blogs[index],
      liked: !liked,
      likes: liked ? likes - 1 : likes + 1,
    };
    setBlogs(updatedBlogs);

    // Update the API with the new liked state and like count
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${blogId}/`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            liked: !liked,
            likes: liked ? likes - 1 : likes + 1,
          }),
        }
      );

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {error && <div>{error}</div>}
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <span>{blog.likes}</span>
          <IconButton onClick={() => handleLikeClick(blog.id)}>
            <FavoriteIcon color={blog.liked ? "inherit" : "error"} />
          </IconButton>
        </div>
      ))}
    </div>
  );
}
