import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function Signup() {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [passwordValidation, setPasswordValidation] = useState("");
    const [emailValidation, setEmailValidation] = useState("");
    const [confirmPwdMsg, setConfirmPwdMsg] = useState("");
    const [register, setRegister] = useState(false);
    const [message, setMessage] = useState("");
    const [apiError, setApiError] = useState("");
    const [userErrorMessage,setUserErrorMessage] = useState("");
    const [usernameExistsError, setUsernameExistsError] = useState(null);
    const [errorMsg, setErrorMsg] = useState("");
    const [errorMessage, setErrorMessage] = useState('');
    const [emailExist,setEmailExist] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        // added check for empty input fields
        if(!username || !password || !email || !confirmPassword) {setErrorMsg("Please Fill the Fields!")}


        const fetchdata = fetch("http://localhost:8000/api/register/", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username, email, password }),
            }).then((response) => {
                response.json().then((data) => {
                    console.log(data);
                    if (data.username) {
                      console.log(data.username);
                      setErrorMessage(data.username);
                    } else if (data.email) {
                      console.log(data.email);
                      setEmailExist(data.email);
                    }
                  }).catch(error => {
                    console.error(error);
            });

                if (response.status == 201 || response.ok) {
                    setRegister(true);
                    setMessage("Successfully signed up! Please wait while you are redirected to the login page...");
                    setTimeout(() => {
                        navigate("/login");
                    }, 1500);
                } else if (response.status === 409) {
                  // setUserErrorMessage(response.message);
                  setErrorMessage("USERNAME EXISTS");
                  throw new Error('Username is already taken');
                } else if(response.status == 400){
                    // setApiError("Bad request! An error occurred while signing up. Please try again.");
                }
                else {
                  // console.log(response.JSON()); 
                  setApiError("Something went wrong!");
                }
            })

            .catch((error) => {
                console.error(error);
                console.log(error.message);


            });
    };

    const handleUsernameChange = (event) => {

        setUsername(event.target.value);
    };

    const handleEmailChange = (event) => {
      const { value } = event.target;
      setEmail(value);

      // Email validation using regular expression
      const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
      (!emailRegex.test(value)) ? setEmailValidation('Please enter a valid email address.') :  setEmailValidation('');
      setEmail(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
        const passwordRegex = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
        event.target.value.length <= 8 && !passwordRegex.test(password) ? setPasswordValidation("Password must contain at least 8 characters including 1 uppercase, 1 lowercase, 1 digit and 1 special character") : setPasswordValidation("");
    };

    const handleConfirmPasswordChange = (event) => {
        setConfirmPassword(event.target.value);
        event.target.value !== password ? setConfirmPwdMsg("Passwords do not match") : setConfirmPwdMsg("");
    };

    return (
        <div>
            {register ? (
                <div>
                    <h4>{message}</h4>
                </div>
            ) : (
                <form onSubmit={handleSubmit}>
                    <div>                   
                        <label htmlFor="username">Username:</label>
                        {/* {userErrorMessage} {usernameExistsError && <p>{usernameExistsError}</p>}  */}
                        {errorMessage && <div className="validationError">{errorMessage}</div>} 
                        {errorMsg} <br></br>
                        <input type="text" id="username" value={username} onChange={handleUsernameChange} />
                    </div>
                    <div>

                        <label htmlFor="email">Email:</label>
                        <div className="validationError">{emailValidation} {errorMsg} {emailExist}  </div><br />
                        <input type="email" id="email" value={email} onChange={handleEmailChange} />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        {passwordValidation && <span>{passwordValidation}</span>} {errorMsg} <br></br>
                        <input type="password" id="password" value={password} onChange={handlePasswordChange} />
                    </div>
                    <div>
                        <label htmlFor="password">Confirm Password:</label>
                        <div className="validationError">{confirmPwdMsg && <span>{confirmPwdMsg}</span>} {errorMsg} </div><br />
                        <input type="password" id="confirmPassword" value={confirmPassword} onChange={handleConfirmPasswordChange} />
                    </div>

                    {apiError && <h4>{apiError}</h4>}

                    <div>
                        <button type="submit">Signup</button>
                        <h5>Already Have an Account?</h5>
                        <Link to="/login"><button> Login </button></Link>
                    </div>
                </form>
            )}

        </div>
    );
}

export default Signup;
