import React, { useEffect, useState } from 'react';

const Likes = () => {
  const [blogs, setBlogs] = useState([]);
  const [likes, setLikes] = useState([]);
  const [liked, setLiked] = useState([]);

  useEffect(() => {
    const fetchLikes = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/likes/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          const blogLikes = data.map((blog) => ({
            id: blog.id,
            likes: blog.likes,
            liked_state: blog.liked_state,
            title: blog.title,
            image: blog.image,
            content: blog.content,
            authorName: blog.authorName,
            created_at: blog.created_at,
            updated_at: blog.updated_at,
          }));

          setBlogs(blogLikes);
          setLikes(blogLikes.map((blog) => blog.likes));
          setLiked(blogLikes.map((blog) => blog.liked_state));

          console.log(blogLikes);
          setShareUrls(blogLikes.map((blog) => `${window.location.origin}/blog/${blog.id}`));
        } else {
          console.error("Failed to fetch blog likes");
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchLikes();
  }, []);

  useEffect(() => {
    fetchComments();
  }, []);

  const fetchComments = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/blog/comments/', {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('accessToken')}`,
        },
      });

      if (response.ok) {
        const commentsData = await response.json();
        console.log('comments are ', commentsData);

        // Merge the comments with the existing blogs data
        const updatedBlogs = blogs.map((blog) => {
          const comments = commentsData.filter((comment) => comment.blog === blog.id);
          return {
            ...blog,
            comments: comments,
          };
        });

        setBlogs(updatedBlogs);
      } else {
        console.error('Failed to fetch comments');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLike = async (blogId, currentLikedState, index) => {
    try {
      const response = await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        },
        body: JSON.stringify({ liked: !currentLikedState }),
      });

      if (response.ok) {
        // Update liked state for the current blog
        const updatedBlogLikes = [...blogs];
        updatedBlogLikes[index].liked_state = !currentLikedState;
        if (currentLikedState) {
          updatedBlogLikes[index].likes--;
        } else {
          updatedBlogLikes[index].likes++;
        }
        setBlogs(updatedBlogLikes);

        // Update total likes count
        const updatedLikes = [...likes];
        if (currentLikedState) {
          updatedLikes[index]--;
        } else {
          updatedLikes[index]++;
        }
        setLikes(updatedLikes);

        // Update liked state
        const updatedLiked = [...liked];
        updatedLiked[index] = !currentLikedState;
        setLiked(updatedLiked);
      } else {
        console.error("Error updatinglike state");
      }
      } catch (error) {
      console.error(error);
      }
      };

      return (
      <div>
      {blogs.map((blog, index) => (
      <div key={blog.id}>
      <h2>{blog.title}</h2>
      <p>{blog.content}</p>
      <p>Author: {blog.authorName}</p>
      <p>Likes: {blog.likes}</p>
      <button onClick={() => handleLike(blog.id, blog.liked_state, index)}>
      {blog.liked_state ? "Unlike" : "Like"}
      </button>
      <h3>Comments:</h3>
      {blog.comments &&
      blog.comments.map((comment) => (
      <div key={comment.id}>
      <p>Comment by: {comment.author}</p>
      <p>{comment.comment_content}</p>
      <p>Created at: {comment.comment_created_at}</p>
      </div>
      ))}
      </div>
      ))}
      </div>
      );
      };

      export default Likes;
