import { useState, useEffect } from "react";

function BlogList() {
  const [blogs, setBlogs] = useState([]);
  const [likes, setLikes] = useState(0);
  const [blogId, setBlogId] = useState();


  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },

        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        console.log(data); // log the data to the console
        setBlogs(data); // update the state with the fetched data
        setLikes(data.likes);
        setBlogId(data.author_id);

      } catch (error) {
        console.error("Error fetching blogs:", error);
      }
    };

    fetchBlogs();
  }, []);


  const handleLike = async (blogId) => {
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${blogId}/`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            likes: blogs.find((blog) => blog.id === blogId).likes + 1,
          }),
        }
      );
      const data = await response.json();
      setBlogs((prevBlogs) =>
        prevBlogs.map((blog) =>
          blog.id === blogId ? { ...blog, likes: data.likes } : blog
        )
      );
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <p>likes{blog.likes}</p>
          {blog.author_id}
          <p>liked by {blog.liked_by}</p>
          <div>

  <button onClick={handleLike}>Like</button>
</div>

        </div>
      ))}
    </div>
  );
}

export default BlogList;
