import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import CommentIcon from "@mui/icons-material/Comment";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Popover from '@mui/material/Popover';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);
  const [copied, setCopied] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);

  const [likes, setLikes] = useState([]);
  const [liked, setLiked] = useState([]);


  const copyToClipboard = (text, index, event) => {
    navigator.clipboard.writeText(text);
    let newBlogs = [...blogs];
    newBlogs[index].copied = true;
    setBlogs(newBlogs);
    setCopied(true);
    setCopySuccess(true);
  };

  const cancelCopy = () => {
    setCopySuccess(false);
    setCopied(false);
  };

  useEffect(() => {
    const fetchLikes = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/likes/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        const blogLikes = data.map((blog) => {
          return {
            id: blog.id,
            likes: blog.likes,
            liked_state: blog.liked_state,
            title: blog.title,
            image: blog.image,
            content: blog.content,
            authorName: blog.authorName,
            created_at: blog.created_at,
            updated_at: blog.updated_at,
            copied: false, // initialize the copied state for each blog post to false
          };
        });

        setBlogs(blogLikes);
        setLikes(blogLikes.map((blog) => blog.likes));
        setLiked(blogLikes.map((blog) => blog.liked_state));
        console.log(blogLikes);
      } catch (error) {
        console.error(error);
      }
    };

    fetchLikes();
  }, []);

  return (
    <div>
      {blogs.map((blog, index) => (
        <div key={blog.id}>
        <IconButton
          aria-describedby={`popover-${index}`}
          onClick={(event) => {
            setAnchorEl(event.currentTarget);
            setCopied(false);
          }}
        >
          <ShareIcon color="action" style={{ width: "30px", height: "30px" }} />
        </IconButton>
        <Popover
          open={Boolean(anchorEl) && anchorEl === event.currentTarget}
          anchorEl={anchorEl}
          onClick={(event) => {
            copyToClipboard(`${window.location.origin}/blog/${blog.id}`, index, event);
            let newBlogs = [...blogs];
            newBlogs[index].copied = true;
            setBlogs(newBlogs);
          }}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center",
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "center",
          }}
        >
          {copied && (
            <Typography sx={{ p: 2 }}>Link copied to clipboard!</Typography>
          )}

          <Typography sx={{ p: 2 }}>{`${window.location.origin}/blog/${blog.id}`}</Typography>

          <Button
variant="contained"
disabled={blog.copied} // use the copied state of the blog post
onClick={() => {
  copyToClipboard(`${window.location.origin}/blog/${blog.id}`);
  let newBlogs = [...blogs];
  newBlogs[index].copied = true; // set the copied state of the corresponding blog post to true
  setBlogs(newBlogs);
}}
>
{blog.copied ? "Copied" : "Copy"} {/* Use the copied state of the blog post to show "Copied" or "Copy" on the button */}
</Button>
</Popover>
</div>
      ))}


</div>
  )}
