import React, { useState } from 'react';
import Blog from './blog';
import { Link, Navigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // added state for redirecting
  const [formSubmitted, setFormSubmitted] = useState(false); // added state for form submission

  const [errorMsg, setErrorMsg] = useState('');
  const handleUsernameChange =(event) => {
    if (username.length < 5 && formSubmitted) {
      setUsrErrorMessage('Username should have at least 5 characters');
    } else if(username.length > 5){
      setUsrErrorMessage('');
    }
    setUsername(event.target.value)
  };

  const handlePasswordChange = (event) => {
    if(password.length < 8 && formSubmitted){
      setPwdErrorMessage('password should have at least 8 characters');
    } else if(password.length > 8){
      setPwdErrorMessage('');
    }
    setPassword(event.target.value);
  };

  const [pwdErrorMessage, setPwdErrorMessage] = useState('');
  const [usrErrorMessage,setUsrErrorMessage] = useState('');


  const handleSubmit = (event) => {
    event.preventDefault();
    setFormSubmitted(true);

    fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      })
      .then(response => {
        if (response.ok) {
          return response.json()
        } else if(response.status === 401){
           setErrorMsg("unauthorized user");
        } else if(response.status === 400){
          setErrorMsg("Bad request");
        }
        else {
          setErrorMsg("server error!");
          throw new Error('Login failed')
        }
      })
      .then(data => {
        // Save the refresh and access tokens in local storage or cookies
        localStorage.setItem('refreshToken', data.refresh)
        localStorage.setItem('accessToken', data.access)
        setLoggedIn(true);
        // Redirect to dashboard or homepage
      //  alert("token");
      })
      .catch(error => {
        console.error(error);
      })
    }

  if (loggedIn) { 
    // redirect to Dashboard if loggedIn is true
    return <Navigate to="/blog"/>
  }


  return (

    <form onSubmit={handleSubmit}>
     <h2>Login</h2>
      <div>
        <label htmlFor="username">Username:</label> <br></br>
        {formSubmitted && usrErrorMessage} <br></br>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label><br></br>
        {formSubmitted && pwdErrorMessage}<br></br>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>

      {errorMsg}

      <button type="submit">Log in</button>
    </form>
  );
}

export default Login;
