import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLike = async (blogId) => {
    // Find the blog post to update
    const blog = blogs.find((blog) => blog.id === blogId);

    // Calculate the new like count and liked state
    const newLikes = blog.liked ? blog.likes - 1 : blog.likes + 1;
    const newLiked = !blog.liked;

    // Update the local state immediately
    const updatedBlogs = blogs.map((blog) => {
      if (blog.id === blogId) {
        return {
          ...blog,
          liked: newLiked,
          likes: newLikes,
        };
      }
      return blog;
    });
    setBlogs(updatedBlogs);

    // Call API to update like state
    await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        author_id: blog.author_id,
        likes: newLikes,
        liked: newLiked,
      }),
    });
  };

  return (
    <div>
      {error && <p>{error}</p>}
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id}>

            {blog.liked_by.includes(blog.author) ? 
            <div> 
               </div> : <div> 
              </div>}

            <IconButton>
              <ShareIcon />
            </IconButton>
          </div>
        ))}
      </div>
    </div>
  );
}
