import { useState } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [liked, setLiked] = useState(false);
  const [likes, setLikes] = useState(0);

  // const handleLikeClick = () => {
  //   if (liked) {
  //     // User has already liked the post, so remove like
  //     setLiked(false);
  //     setLikes((prevLikes) => prevLikes - 1);
  //   } else {
  //     // User has not liked the post, so add like
  //     setLiked(true);
  //     setLikes((prevLikes) => prevLikes + 1);
  //   }
  // };
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLikeClick = async (blogId) => {
    if (liked) {
      // User has already liked the post, so remove like
      setLiked(false);
      setLikes((prevLikes) => prevLikes - 1);
    } else {
      // User has not liked the post, so add like
      setLiked(true);
      setLikes((prevLikes) => prevLikes + 1);
    }

    // Update the API with the new liked state and like count
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${blogId}/`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            liked: liked,
            likes: likes,
          }),
        }
      );

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div>

    </div>
  );
}
