import React, { useState, useEffect } from "react";

function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [userId, setUserId] = useState(null);

  const [error, setError] = useState(null);
  const [countLikes, setCountLikes] = useState(0);
  const [likedBy, setLikedBy] = useState(0);
  const [authorId, setAuthorId] = useState(null);

  useEffect(() => {
    // Fetch userId from API
    fetch("http://localhost:8000/api/user/")
      .then((response) => response.json())
      .then((data) => {
        setUserId(data.userId);
      })
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);

        setCountLikes(blogsWithLiked[0].likes);
        setLikedBy(blogsWithLiked.liked_by);

        setAuthorId(blogsWithLiked.author);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLikeClick = (blogId) => {
    const blogToUpdate = blogs.find((blog) => blog.id === blogId);

    if (blogToUpdate.liked_by.includes(userId)) {
      // User has already liked the blog, so remove like
      blogToUpdate.liked_by = blogToUpdate.liked_by.filter(
        (id) => id !== userId
      );
      blogToUpdate.likes -= 1;
    } else {
      // User has not liked the blog, so add like
      blogToUpdate.liked_by.push(userId);
      blogToUpdate.likes += 1;
    }

    // Update likes in API
    fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        likes: blogToUpdate.likes,
        liked_by: blogToUpdate.liked_by,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setBlogs((prevBlogs) =>
          prevBlogs.map((blog) => (blog.id === data.id ? data : blog))
        );
      })
      .catch((error) => console.log(error));
  };

  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <span>{blog.likes}</span>
          <IconButton onClick={() => handleLikeClick(blog.id)}>
            {blog.liked_by.includes(userId) ? (
              <FavoriteIcon color="error" />
            ) : (
              <FavoriteIcon />
            )}
          </IconButton>
          <IconButton>
            <ShareIcon />
          </IconButton>
        </div>
      ))}
    </div>
  );
}

export default Likes;
