import React, { useState } from 'react';
import Blog from './blog';
import { Link, Navigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // added state for redirecting

  const handleUsernameChange = event => setUsername(event.target.value);
  const handlePasswordChange = event => setPassword(event.target.value);
  const [message, setMessage] = useState('');
  const [apiErrorMsg, setApiErrorMsg] = useState('');


  const handleSubmit = event => {
    event.preventDefault();
    fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      })
      .then(response => {
        if (response.ok) {
          return response.json()
        } else {
          setMessage('');
          setApiErrorMsg('server error');
          throw new Error('Login failed!Something went wrong')
        }
      })
      .then(data => {
        // Save the refresh and access tokens in local storage or cookies
        localStorage.setItem('refreshToken', data.refresh)
        localStorage.setItem('accessToken', data.access)
        setLoggedIn(true);
        setMessage('successful login');
        setApiErrorMsg('');
        // Redirect to dashboard or homepage
      //  alert("token");
      })
      .catch(error => {
        setApiErrorMsg('');
        setMessage('unathorized');
        console.error(error);

      })
    }

  if (loggedIn) { 
    // redirect to Dashboard if loggedIn is true
    return <Navigate to="/blog"/>
  }


  return (

    <form onSubmit={handleSubmit}>
     <h2>Login</h2>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      <p>{apiErrorMsg}</p>
      <p>{message}</p>
      <button type="submit">Log in</button>


      <h4>Don't have an account? Create an account?</h4> 
      <Link to="/signup">Signup</Link>
    </form>
  );
}

export default Login;
