import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

export default function Likes(props) {
  const [liked, setLiked] = useState(false);
  const [likes, setLikes] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Retrieve initial like state and count from API
    setLoading(true);
    fetch('http://localhost:8000/api/blog/likes/', {
      headers: {
        Authorization: `Bearer ${props.accessToken}`,
      },
    })
      .then(response => response.json())
      .then(data => {
        setLiked(data.liked);
        setLikes(data.likes);
        setLoading(false);
      })
      .catch(error => {
        console.error(error);
        setLoading(false);
      });
  }, [props.accessToken]);

  const handleLike = () => {
    // Update like state and count in API
    setLoading(true);
    fetch(`http://localhost:8000/api/blog/likes/update/${props.blogId}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${props.accessToken}`,
      },
      body: JSON.stringify({
        author_id: props.authorId,
        username: props.username,
        likes: likes + (liked ? -1 : 1),
        liked: !liked,
      }),
    })
      .then(response => response.json())
      .then(data => {
        setLiked(data.liked);
        setLikes(data.likes);
        setLoading(false);
      })
      .catch(error => {
        console.error(error);
        setLoading(false);
      });
  };

  return (
    <button onClick={handleLike} disabled={loading}>
      {liked ? 'Unlike' : 'Like'} ({likes})
    </button>
  );
}
