import React, { useEffect, useState, useRef } from "react";
import { IconButton, Typography, Slider } from "@mui/material";
import { PlayArrow, Pause, Replay } from "@mui/icons-material";

const changeVolume = (videoId, volume) => {
  const video = document.getElementById(videoId);
  if (video) {
    video.volume = volume / 100;
  }
};

const togglePlayPause = (videoId, setPlaying) => {
  const video = document.getElementById(videoId);
  if (video) {
    if (video.paused || video.ended) {
      video.play();
      setPlaying(true);
    } else {
      video.pause();
      setPlaying(false);
    }
  }
};

const resetVideo = (videoId, setPlaying) => {
  const video = document.getElementById(videoId);
  if (video) {
    video.currentTime = 0;
    video.pause();
    setPlaying(false);
  }
};

function formatTime(time) {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${padZero(minutes)}:${padZero(seconds)}`;
}

function padZero(value) {
  return value.toString().padStart(2, "0");
}

const Likes = () => {
  const videoRefs = useRef([]);
  const [playing, setPlaying] = useState(false);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const updateCurrentTime = () => {
      const video = videoRefs.current.find((ref) => ref && ref.readyState === 4);
      if (video) {
        setCurrentTime(video.currentTime);
      }
    };

    const updateDuration = () => {
      const video = videoRefs.current.find((ref) => ref && ref.readyState === 4);
      if (video) {
        setDuration(video.duration);
      }
    };

    const interval = setInterval(updateCurrentTime, 100);
    window.addEventListener("resize", updateDuration);

    return () => {
      clearInterval(interval);
      window.removeEventListener("resize", updateDuration);
    };
  }, []);

  useEffect(() => {
    const fetchLikesAndComments = async () => {
      try {
        const likesResponse = await fetch("http://localhost:8000/api/blog/likes/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const commentsResponse = await fetch("http://localhost:8000/api/blog-comments/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        if (likesResponse.ok && commentsResponse.ok) {
          const likesData = await likesResponse.json();
          const blogLikes = likesData.map((blog) => {
            return {
              id: blog.id,
              likes: blog.likes,
              liked_state: blog.liked_state,
              title: blog.title,
              image: blog.image,
              content: blog.content,
              authorName: blog.authorName,
              created_at: blog.created_at,
              total_comments: blog.total_comments,
              video: blog.video,
            };
          });

          setBlogs(blogLikes);
        } else {
          console.error("Failed to fetch blogs and comments");
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchLikesAndComments();
  }, []);

  const handleSliderChange = (event, value, index) => {
    const video = videoRefs.current[index];
    if (video) {
      video.currentTime = value;
      setCurrentTime(value);
    }
  };

  const playVideo = (videoId) => {
    const video = document.getElementById(videoId);
    video.play();
  };

  const pauseVideo = (videoId) => {
    const video = document.getElementById(videoId);
    video.pause();
  };

  return (
    <div>
      {blogs.map((blog, index) => (
        <div key={blog.id}>
          {blog.title}
          <div>
            {blog.video && (
              <div>
                <video
                  id={`video-${blog.video}`}
                  ref={(ref) => (videoRefs.current[index] = ref)}
                  style={{ maxWidth: "100%" }}
                  onPlay={() => setPlaying(true)}
                  onPause={() => setPlaying(false)}
                  onLoadedMetadata={() =>
                    setDuration(videoRefs.current[index].duration)
                  }
                >
                  <source src={`http://localhost:8000${blog.video}`} type="video/mp4" />
                </video>
                <div style={{ display: "flex", alignItems: "center" }}>
                  <IconButton
                    onClick={() => togglePlayPause(`video-${blog.video}`, setPlaying)}
                  >
                    {playing ? <Pause /> : <PlayArrow />}
                  </IconButton>
                  <IconButton
                    onClick={() => resetVideo(`video-${blog.video}`, setPlaying)}
                  >
                    <Replay />
                  </IconButton>
                  <div style={{ flexGrow: 1 }}>
                    <Slider
                      value={currentTime}
                      max={duration}
                      onChange={(event, value) => handleSliderChange(event, value, index)}
                      aria-labelledby="video-slider"
                    />
                  </div>
                  <Typography variant="caption">
                    {formatTime(currentTime)} / {formatTime(duration)}
                  </Typography>
                </div>
                <div>
                  <span>Volume:</span>
                  <Slider
                    defaultValue={50}
                    aria-labelledby="volume-slider"
                    onChange={(event, value) =>
                      changeVolume(`video-${blog.video}`, value)
                    }
                  />
                </div>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Likes;
