import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';


function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState(null);
  const [liked, setLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        setBlogs(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBlogs();
  }, []);

  const handleBlogClick = (id) => {
    const blog = blogs.find((blog) => blog.id === id);
    setSelectedBlogId(id);
    setLiked(blog.liked);
    setLikeCount(blog.likes);
  };

  const handleLikeClick = async (blog) => {
    const { id, liked } = blog;
    const newLiked = !liked;
    const newLikes = liked ? blog.likes - 1 : blog.likes + 1;

    try {
      const response = await fetch(`http://localhost:8000/api/blog/likes/update/${id}/`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        },
        body: JSON.stringify({ liked: newLiked, likes: newLikes }),
      });

      const data = await response.json();
      const newBlogs = blogs.map((blog) => (blog.id === data.id ? data : blog));

      setBlogs(newBlogs);
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div>
      <h2>Blogs</h2>
      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p>Likes: {blog.likes}</p>
            <IconButton onClick={() => handleLikeClick(blog.id)}>
              <FavoriteIcon color={blog.liked ? "error" : "primary"} />
            </IconButton>
          </div>
        ))}
      </div>

      {selectedBlogId && (
        <div className="likes">
          <h3>
            {blogs.find((blog) => blog.id === selectedBlogId).title} - Likes:{" "}
            {likeCount}
          </h3>
          <IconButton
            onClick={() => {
              setLikeCount(likeCount + (liked ? -1 : 1));
              setLiked(!liked);
            }}
          >
            <FavoriteIcon color={liked ? "error" : "primary"} />
          </IconButton>
          <IconButton onClick={handleLikeClick}>
            <ShareIcon />
          </IconButton>
        </div>
      )}
    </div>
  );
}

export default Likes;
