function Likes(props) {
  const [likedPost, setLikedPost] = useState(props.liked);
  const [Likeblogs, setLikeBlogs] = useState();
  const [blogId, setBlogIds] = useState();
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();
        console.log(data);

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));
        setLikedPost(blogsWithLiked);

        // Store all the blog IDs in an array
        const blogIds = data.map((blog) => blog.id);
        setBlogIds(blogIds);
      } catch (error) {
        console.error(error);
      }
    };

    fetchBlogs();
  }, []);

   console.log(blogId);

  const handleLikeClick = () => {
    const newLikedState = !likedPost;
    setLikedPost(newLikedState);

    const data = {
      author_id: props.author_id,
      blog_name: props.blog_name,
      likes: props.likes + (newLikedState ? 1 : -1),
      liked: newLikedState
    }




    fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return response.json();
    })
    .then(data => console.log(data))
    .catch(error => console.error(error));
  };

  return (
    <div>
      {blogs
    .sort((a, b) => b.id - a.id)
   .map((blog) => (
       <div key={blog.id} style={{ }}>
           <h2>{blog.title}</h2>
           <img src={`http://localhost:8000${blog.image}`} style={{  width: "100%",maxWidth: "100%" }} />
          <div style={{  padding: "1rem 0 0 0rem", wordSpacing: "0.1em",textAlign: "justify"}} > 
           <h5>Author: {blog.authorName}</h5>
            <p >{blog.content}</p>

           <p>Published Date: {blog.created_at}, Updated Date: {blog.updated_at} </p>
            {created_at && <p>Blog Created at Standard: {created_at}</p>}

       <p>likes:{blog.likes}</p>
       <p>liked_by:{blog.liked_by}</p>
       <p>liked_state: {blog.liked_state} </p>
        {/* <IconButton aria-label="share">
          <ShareIcon />
        </IconButton> */}
                <h2>{props.blog_name}</h2>
      <p>Author: {props.author}</p>
      <p>Likes: {props.likes}</p>
      <button onClick={handleLikeClick}>
        {setLikedPost ? 'Unlike' : 'Like'}
      </button>
            </div>
            <hr></hr>

        </div>

    ))} 

    </div>
  );
}

export default Likes;
