import React, { useState, useEffect } from 'react';

export default function UpdateBlog() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedBlogId, setSelectedBlogId] = useState('');
  const [updatedBlogTitle, setUpdatedBlogTitle] = useState('');
  const [updatedBlogContent, setUpdatedBlogContent] = useState('');
  const [updatedBlogAuthorName, setUpdatedBlogAuthorName] = useState('');

  useEffect(() => {
    fetch('http://localhost:8000/api/blog/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      setBlogs(data);
      setLoading(false);
    })
    .catch(error => console.error(error));
  }, []);

  const handleUpdate = () => {
    const updatedBlog = {
      id: selectedBlogId,
      title: updatedBlogTitle === '' ? '' : updatedBlogTitle || blogs.find(blog => blog.id === selectedBlogId).title,
      content: updatedBlogContent === '' ? '' : updatedBlogContent || blogs.find(blog => blog.id === selectedBlogId).content,
      authorName: updatedBlogAuthorName === '' ? '' : updatedBlogAuthorName || blogs.find(blog => blog.id === selectedBlogId).authorName
    };

    fetch(`http://localhost:8000/api/blog/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(updatedBlog)
    })
    .then(response => response.json())
    .then(data => {
      const index = blogs.findIndex(blog => blog.id === data.id);
      const newBlogs = [...blogs];
      newBlogs[index] = data;
      setBlogs(newBlogs);
      setSelectedBlogId('');
      setUpdatedBlogTitle('');
      setUpdatedBlogContent('');
      setUpdatedBlogAuthorName('');
    })
    .catch(error => console.error(error));
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>Blog List</h1>
      <ul>
        {blogs.map(blog => (
          <li key={blog.id}>
            <h2>{blog.title}</h2>
            <p>{blog.content}</p>
            <p>Author: {blog.authorName}</p>
            <button onClick={() => handleUpdate(blog.id)}>Edit</button>
          </li>
        ))}
      </ul>
      {selectedBlogId && (
        <div>
          <h2>Edit Blog</h2>
          {blogs.map(blog => {
            if (blog.id === selectedBlogId) {
              return (
                <div key={blog.id}>
                  <label>
                    Title:
                    <input
                      type="text"
                      value={updatedBlogTitle === '' ? blog.title : updatedBlogTitle}
                      onChange={(event) => setUpdatedBlogTitle(event.target.value)}
                    />
                  </label>
                  <br />
                  <label>
                    Content:
                    <textarea
                      value={updatedBlogContent === '' ? blog.content : updatedBlogContent}
                      onChange={(event) => setUpdatedBlogContent(event.target.value)}
                    />
                  </label>
                  <br />
                  <label>
                    Author Name:
                    <input
                      type="text"
                      value={updatedBlogAuthorName === '' ? blog.authorName : updatedBlogAuthorName}
                      onChange={(event) => setUpdatedBlogAuthorName(event.target.value)}
                    />
                  </label>
                  <br />
                </div>
              );
            } else {
              return null;
            }
          })}
          <button onClick={() => handleUpdate(selectedBlogId)}>Save Changes</button>
        </div>
      )}
    </div>
  )}
