import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Update() {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState('');
  const [updatedBlogTitle, setUpdatedBlogTitle] = useState('');
  const [updatedBlogContent, setUpdatedBlogContent] = useState('');
  const [updatedBlogAuthorName, setUpdatedBlogAuthorName] = useState('');
  const [updatedBlogImage, setUpdatedBlogImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    // Fetch blogs data and update state
    fetch('http://localhost:8000/api/blog/list/', {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('accessToken')}`,
      },
    })
      .then((response) => response.json())
      .then((data) => setBlogs(data))
      .catch((error) => console.error(error));
  }, []);

  const handleUpdate = (event) => {
    event.preventDefault();
    const blogToUpdate = blogs.find((blog) => blog.id === selectedBlogId);

    const updatedBlog = {
      id: selectedBlogId,
      title: updatedBlogTitle || blogToUpdate.title,
      content: updatedBlogContent || blogToUpdate.content,
      authorName: updatedBlogAuthorName || blogToUpdate.authorName,
      image: updatedBlogImage || blogToUpdate.image,
    };

    const formData = new FormData();
    formData.append('title', updatedBlog.title);
    formData.append('content', updatedBlog.content);
    formData.append('authorName', updatedBlog.authorName);
    formData.append('image', updatedBlogImage);

    fetch(`http://localhost:8000/api/blog/update/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('accessToken')}`,
      },
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        const updatedBlogIndex = blogs.findIndex((blog) => blog.id === data.id);
        const updatedBlogs = [...blogs];
        updatedBlogs[updatedBlogIndex] = data;
        setBlogs(updatedBlogs);
        setSelectedBlogId('');
        setUpdatedBlogTitle('');
        setUpdatedBlogContent('');
        setUpdatedBlogAuthorName('');
        setUpdatedBlogImage(null);
        setImageUrl(null);
      })
      .catch((error) => console.error(error));
  };

  const cancelUpdate = () => {
    setSelectedBlogId('');
    setUpdatedBlogTitle('');
    setUpdatedBlogContent('');
    setUpdatedBlogAuthorName('');
    setUpdatedBlogImage(null);
    setImageUrl(null);
  };

  // Check if there is a previously saved image for the record and prepopulate the image upload field
  useEffect(() => {
    if (selectedBlogId !== '') {
      setImageUrl(blogs.find((blog) => blog.id === selectedBlogId).image);
    }
  }, [selectedBlogId]);

  const handleFileUpload = (event) => {
    setUpdatedBlogImage(event.target.files[0]);
  };

  return (
    <div>
    <div>
  <Link to={'/imageUpload'}>Image Upload</Link>
  <form onSubmit={handleUpdate} encType='multipart/form-data'>
    {selectedBlogId !== '' && (
      <h2>Update Blog "{blogs.find((blog) => blog.id === selectedBlogId).title}"</h2>
    )}
    <label htmlFor='blog-title'>Title:</label>
    <input
      type='text'
      id='blog-title'
      value={updatedBlogTitle || blogs.find((blog) => blog.id === selectedBlogId).title}
      onChange={(e) => setUpdatedBlogTitle(e.target.value)}
    />
    <label htmlFor='blog-author'>Author:</label>
    <input
      type='text'
      id='blog-author'
      value={updatedBlogAuthorName || blogs.find((blog) => blog.id === selectedBlogId).authorName}
      onChange={(e) => setUpdatedBlogAuthorName(e.target.value)}
    />
    <label htmlFor='blog-image'>Image:</label>
    <input
      type='file'
      id='blog-image'
      onChange={handleFileUpload}
    />
    {imageUrl && (
      <img
        src={imageUrl}
        alt='Blog thumbnail'
        style={{ width: '200px', marginTop: '10px' }}
      />
    )}
    <button type='submit' className='btn btn-primary'>
      Update
    </button>
    <button type='button' className='btn btn-secondary ml-2' onClick={cancelUpdate}>
      Cancel
    </button>
  </form>
</div>


    </div>
    );
}

export default Update;
